@mixin icon-circle($size: 18px, $color: $color-meta) {
  display: inline-block;
  width: $size;
  height: $size;
  overflow: hidden;
  padding: 0;
  line-height: $size - 4px;
  text-align: center;
  border-radius: 50%;
  border: 2px solid $color;
  color: $color;
}



@mixin icon-arrow-down($fill-color: null, $size: 4px) {
  width: 0;
  height: 0;
  border-left: rem-calc($size) solid transparent;
  border-right: rem-calc($size) solid transparent;

  @if $fill-color == null {
    border-top: rem-calc($size) solid;
  } @else {
    border-top: rem-calc($size) solid $fill-color;
  }
}

@mixin icon-check-mark($fill-color) {
  @extend %background-position-svg;
  background-image: url('data:image/svg+xml;utf8,<svg fill="#{$fill-color}" version="1.1" xmlns="http://www.w3.org/2000/svg" width="44" height="32" viewBox="0 0 44 32"><path d="M41.186 1.67c-1.936-1.937-5.075-1.937-7.011 0l-17.239 17.237-8.472-8.472c-1.935-1.937-5.076-1.937-7.011 0s-1.936 5.075 0 7.011l11.978 11.979c0.968 0.969 2.237 1.452 3.506 1.452s2.538-0.483 3.506-1.452l20.744-20.744c1.937-1.936 1.937-5.075 0.001-7.011z"></path></svg>');
}

@mixin icon-email($fill-color) {
  @extend %background-position-svg;
  background-image: url('data:image/svg+xml;utf8,<svg fill="#{$fill-color}" version="1.1" xmlns="http://www.w3.org/2000/svg" width="43" height="32" viewBox="0 0 43 32"><path d="M40 0h-37.333c-1.475 0-2.667 1.192-2.667 2.667v26.667c0 1.475 1.192 2.667 2.667 2.667h37.333c1.475 0 2.667-1.192 2.667-2.667v-26.667c0-1.475-1.192-2.667-2.667-2.667v0zM37.333 5.333v0.176l-16 12.445-16-12.445v-0.176h32zM5.333 26.667v-14.4l14.363 11.171c0.48 0.376 1.059 0.563 1.637 0.563s1.157-0.187 1.637-0.563l14.363-11.171v14.4h-32z"></path></svg>');
}

@mixin icon-password($fill-color) {
  @extend %background-position-svg;
  background-image: url('data:image/svg+xml;utf8,<svg fill="#{$fill-color}" version="1.1" xmlns="http://www.w3.org/2000/svg" width="24" height="32" viewBox="0 0 24 32"><path d="M22 12h-2v-4c0-4.418-3.582-8-8-8s-8 3.582-8 8v4h-2c-1.106 0-2 0.894-2 2v16c0 1.106 0.894 2 2 2h20c1.106 0 2-0.894 2-2v-16c0-1.106-0.894-2-2-2v0zM8 8c0-2.208 1.792-4 4-4s4 1.792 4 4v4h-8v-4zM4 16h16v12h-16v-12z"></path><path d="M12 24c1.106 0 2-0.894 2-2s-0.894-2-2-2c-1.106 0-2 0.894-2 2s0.894 2 2 2z"></path></svg>');
}

/* scss-combine:general/extends */
/*----------  Text treatments  ----------*/

%body-text-treatment {
  color: $color-body;
  font-family: $font-body;
  font-weight: 300;
  font-size: $font-size-body;
  line-height: 1.625;
}

%heading-text-treatment {
  text-transform: uppercase;
  letter-spacing: 0.05em;
  font-family: $heading-font;
  font-weight: $heading-weight;
  color: $heading-color;
}

%h1 {
  font-family: $font-secondary;
  font-size: rem-calc(49);
  font-weight: 400;
  line-height: 1.327; // 65

  @if ($font-secondary == "Lato") {
    font-weight: 900;
  }

  @if ($font-secondary == "Playfair Display") {
    font-style: italic;
  }
}

%h2 {
  font-family: $font-secondary;
  font-size: rem-calc(30);
  font-weight: 400;
  line-height: 1.333; // 40

  @if ($font-secondary == "Lato") {
    font-weight: 900;
  }

  @if ($font-secondary == "Playfair Display") {
    font-style: italic;
  }
}

%h3 {
  font-family: $font-primary;
  font-size: rem-calc(24);
  font-weight: 300;
  line-height: 1.333; // 32
  font-style: normal;
}

%h4 {
  font-family: $font-primary;
  font-size: rem-calc(16);
  font-weight: 400;
  line-height: 1.1875; // 19
  font-style: normal;

  @if $font-primary == "Oswald" {
    text-transform: uppercase;
    letter-spacing: 0.09375em;
  }

  @if $font-primary == "Roboto Slab" {
    text-transform: uppercase;
    font-weight: bold;
    letter-spacing: 0.09375em;
  }
}

%h5 {
  font-family: $font-primary;
  font-size: rem-calc(16);
  font-weight: 400;
  line-height: 1.1875; // 19
  font-style: normal;
}

%h6 {
  font-family: $font-secondary;
  font-size: rem-calc(16);
  font-weight: 400;
  line-height: 1.25; // 20

  @if ($font-secondary == "Playfair Display") {
    font-style: italic;
  }
}

%placeholder-text-treatment {
  font-size: rem-calc(12);
  letter-spacing: 0.1666em;
  text-transform: uppercase;
}

%label-text-treatment {
  color: $color-heading;
  font-family: $font-secondary;
  font-size: rem-calc(14);
}

%page-header-treatment {
  padding-top: 4rem;
  text-align: center;

  .show-title-x & {
    padding-top: rem-calc(73);
  }
}

%section-title-text-treatment {
  @extend %h3;
  position: relative;
  text-align: center;

  .show-title-x & {
    &:before {
      position: absolute;
      top: rem-calc(-30);
      left: 50%;
      margin-left: -4px;
      font-size: rem-calc(23);
      line-height: 0;
      color: $color-accent;
      content: "\00D7";
    }
  }
}

%meta-text-treatment {
  text-transform: uppercase;
  letter-spacing: 0.05em;
  font-family: $meta-font;
  font-weight: $meta-weight;
  color: $color-meta;
}

%link-text-treatment {
  font-size: rem-calc(13);
  letter-spacing: 0.115em;
  line-height: 1.154;
}

%link-style {
  @extend %link-text-treatment;
  color: $color-link;
  text-decoration: underline;

  &:hover {
    color: $color-link-hover;
  }
}

%button-primary-style {
  padding: rem-calc(23 34 22);
  font-family: $button-font;
  font-size: rem-calc(13);
  font-weight: $button-weight;
  line-height: 1.154; // 15/13
  letter-spacing: rem-calc(2);
  color: $color-button;
  text-transform: uppercase;
  text-align: center;
  background-color: $color-button-background;
  box-shadow: 0 0 0 1px transparent inset;
  transition: all 0.2s $ease-out-quad;
}

%button-secondary-style {
  color: $color-button-secondary;
  box-shadow: 0 0 0 1px $color-button-secondary inset;
  background-color: transparent;
}

%list-style {
  padding-left: 1.25em;
  margin-bottom: 2em;

  li {
    margin-bottom: 1em;
  }
}

/*----------  Layout  ----------*/

%wrapper {
  @extend %wrapper-padding;
  margin-right: auto;
  margin-left: auto;
  max-width: $max-width;

  /* Solves border-box issue with max-width in IE8 */
  .lt-ie9 & { width: $max-width; }
}

%wrapper-padding {
  padding-left: $gutter;
  padding-right: $gutter;
}

%full-width {
  padding-left: 30px;
  padding-right: 30px;
}

%ghost-center {
  content: " ";
  display: inline-block;
  height: 100%;
  margin-right: -0.275em;
  vertical-align: middle;
}

/*----------  Forms  ----------*/

%form-base-style {
  @include container;
}

/*----------  Utils  ----------*/

%top-bar-sep {
  position: absolute;
  top: 50%;
  width: 1px;
  height: 25px;
  display: inline-block;
  vertical-align: top;
  margin-left: 18px;
  content: '';
  pointer-events: none;
  background-color: rgba(#fff, 0.1);
  transform: translateY(-50%);
}

%fill-parent {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
}

%inline-middle {
  display: inline-block;
  vertical-align: middle;
}

%center-x {
  position: relative;
  left: 50%;
  transform: translateX(-50%);
}

%background-position-svg {
  background-position: 50% 50%;
  background-repeat: no-repeat;
  background-size: contain;
}

%background-position-img {
  background-position: 50% 50%;
  background-repeat: no-repeat;
  background-size: cover;
}

%disable-pointer-events {
  pointer-events: none;
  cursor: default;
}


// Core
/* scss-combine:core/account */
// Default Variables

$account-link-color: #888 !default;
$account-light-color: #fff !default;
$account-accent-color: #5dc5e3 !default;
$account-border-color: #e4e4e4 !default;
$account-gutter: 30px !default;
$account-font-size: 15px !default;
$account-border-radius: 0 !default;
$account-text-color: #5f5f5f !default;

$account-card-background-color: #f5f5f5 !default;

$giftcard-nav-use-default-styles: true !default;

$account-breakpoint-x-small: 400px !default; // the account table columns change to full-width
$account-breakpoint-small: 620px !default; // menu changes from full-width to column
$account-breakpoint-medium: 900px !default; // menu changes from 35% to 25% width

// Internal Variables

$account-font-size-heading: $account-font-size + 2 !default;

$account-text-color-light: lighten($account-text-color,20%) !default;

$account-gutter-inner: $account-gutter / 2 !default;

$auth-gutter: $account-gutter !default;
$auth-font-size: $account-font-size !default;

// Imports

/* scss-combine:account/components/layout */
.account-wrapper {
  @extend %clearfix;
  font-size: $account-font-size;
}

.account-nav,
.account-body {
  vertical-align: top;
}

.account-body {
  @media screen and (min-width: $account-breakpoint-small) {
    float: right;
    width: 65%;
    padding-left: $account-gutter-inner;
  }

  @media screen and (min-width: $account-breakpoint-medium) {
    width: 75%;
  }

  > :first-child {
    margin-top: 0;
  }

  .account-nav-hidden & {
    float: none;
    width: 100%;
    padding-left: 0;
    margin-left: 0;
  }
}

.account-nav {
  @media screen and (max-width: $account-breakpoint-small - 1) {
    margin-bottom: $account-gutter;
  }

  @media screen and (min-width: $account-breakpoint-small) {
    float: left;
    width: 35%;
    padding-right: $account-gutter-inner;
  }

  @media screen and (min-width: $account-breakpoint-medium) {
    width: 25%;
  }
}

/* scss-combine:account/components/forms */
.account-wrapper {
  .form-label-text {
    display: inline-block;
    width: 100%;
    margin-bottom: 5px;
  }

  .form-field-checkbox,
  .form-field-radio {
    .form-label-text { width: auto; }
  }

  .form-field {
    margin-bottom: $account-gutter-inner;
  }

  .form-input {
    width: 100%;

    &.account-form-half {
      width: 50%;
    }
  }

  .form-actions {
    clear: both;
  }
}

.account-form-column-container {
  @extend %clearfix;

  .form-field {
    @media screen and (min-width: 480px) {
      float: left;
      width: 50%;

      &:nth-child(odd) { padding-right: $account-gutter-inner; }
      &:nth-child(even) { padding-left: $account-gutter-inner; }
    }
  }
}

/* scss-combine:account/components/table */
.account-wrapper {
  table {
    width: 100%;
    margin-top: $account-gutter-inner;
    margin-bottom: $account-gutter-inner;
  }

  td,
  th {
    padding: $account-gutter-inner;
  }
}

.account-table-tight {
  th,
  td {
    padding: 10px 0;
    border-bottom: 1px solid $account-border-color;
  }
}

/* scss-combine:account/components/list */
// Layout
// Each list item has 3 sections: thumbnail, body, and badge.

.account-items {
  margin-bottom: $account-gutter;
}

.account-item {
  position: relative;
  display: table;
  width: 100%;
  border-bottom: 1px solid $account-border-color;

  &:first-of-type {
    border-top: 1px solid $account-border-color;
  }
}

.account-item-thumbnail,
.account-item-body,
.account-item-badge,
.account-item-actions {
  padding-top: $account-gutter-inner;
  padding-bottom: $account-gutter-inner;

  @media screen and (min-width: $account-breakpoint-x-small) {
    display: table-cell;
    vertical-align: top;
  }
}

// Link
// Makes the entire item clickable

.account-item-link {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 100;
}

// Thumbnail

.account-item-thumbnail {
  min-width: 100px;

  img {
    height: auto;
    max-width: 100px;
  }
}

// Body

.account-item-body {
  width: 100%;

  @media screen and (min-width: $account-breakpoint-x-small) {
    .account-item-thumbnail + & {
      padding-left: $account-gutter-inner;
    }
  }
}

.account-item-title {
  margin-top: 3px;
  font-size: $account-font-size;

  a {
    color: inherit;
  }
}

.account-item-title-large {
  font-size: $account-font-size * 1.2;
}

.account-item-description {
  margin-top: 0;
  margin-bottom: 5px;
  color: $account-text-color;
}

.account-item-details {
  @extend %clearfix;
  font-size: $account-font-size;
  color: $account-text-color;
}

.account-item-detail {
  float: left;
  margin-top: $account-gutter-inner;
  margin-right: $account-gutter;
}

//scss-lint:disable VariableForProperty
.account-item-detail-title {
  display: block;
  margin-top: 0;
  margin-bottom: 3px;
  font-size: 12px;
  font-weight: normal;
  letter-spacing: 1px;
  color: mix($account-light-color,$account-text-color,60%);
  text-transform: uppercase;
}

// Badge

.account-item-badge,
.account-item-actions {
  white-space: nowrap;
}

/* scss-combine:account/components/header */
.account-header {
  display: table;
  width: 100%;
  margin-bottom: $account-gutter-inner;
}

.account-heading,
.account-toolbar {
  display: table-cell;
  vertical-align: baseline;
}

.account-toolbar {
  text-align: right;

  * {
    display: inline;
  }
}

.account-tool {
  position: relative;
  z-index: 10;
  display: inline-block;
  padding: 0;
  background: transparent;
  margin-left: 10px;
  cursor: pointer;
  color: inherit;
  border: 0;

  &:hover { color: $account-link-color; }

  &:focus { outline: 0; }
}

/* scss-combine:account/components/navigation */
.account-nav-list {
  padding: 0;
  margin-bottom: $account-gutter-inner;
  list-style: none;
  border: 1px solid $account-border-color;
  border-radius: $account-border-radius;
}

.account-nav-item {
  color: $account-link-color;
  border-top: 1px solid $account-border-color;

  &:first-child {
    border-top: 0;
  }

  &.is-active {
    color: $account-accent-color;
    border-left: 3px solid $account-accent-color;
  }
}

.account-nav-link {
  display: block;
  padding: 15px;
  color: inherit;
}

.account-nav-badge {
  float: right;
}

/* scss-combine:account/components/pagination */
.account-wrapper .pagination {
  margin-top: $account-gutter-inner;
}

/* scss-combine:account/components/card */
%account-card-style {
  background: $account-card-background-color;

  .form-input {
    background-color: $account-light-color;
  }
}

.account-card {
  @extend %account-card-style;
  padding: $account-gutter-inner;
  margin: $account-gutter-inner 0;
  border-radius: $account-border-radius;
}

/* scss-combine:account/components/toggle */
// Set the initial toggle state to hidden
.account-toggle-hidden {
  display: none;
}

/* scss-combine:account/components/status */
// Status
// A label used to mark the status of an item

.account-status {
  padding: 6px 10px 5px;
  margin: 0;
  font-size: 12px;
  font-weight: normal;
  line-height: 1;
  letter-spacing: 1px;
  color: $account-light-color;
  text-align: center;
  text-transform: uppercase;
  background: #8e8e8e; // TODO: Colors for each status
}

/* scss-combine:account/components/shipments */
// Shipments
// A list of shipments made for an order. Shown on the Order Details page.

.account-shipments {
  margin-bottom: $account-gutter;
}

.account-shipments-table {
  th,
  td {
    padding: 10px 0;
    text-align: left;
  }

  th {
    padding: $account-gutter-inner 0 0;
    border-top: 1px solid $account-border-color;
  }

  // Override default margins
  .account-wrapper & {
    margin-top: 0;
    margin-bottom: 0;
  }
}


/* scss-combine:account/pages/address */
// Address List

.account-page-add-address {
  .account-content {
    padding-top: $account-gutter-inner;
    border-top: 1px solid $account-border-color;
  }
}

.account-address-detail {
  display: block;
}

// Add Address

.account-address-form {
  padding-top: $account-gutter-inner;

  @media screen and (min-width: $account-breakpoint-medium) {
    .form-field {
      float: left;
      width: 50%;
      padding-right: $account-gutter-inner;

      &:nth-of-type(2n) {
        padding-right: 0;
        padding-left: $account-gutter-inner;
      }
    }
  }

  .form-actions {
    padding-top: $account-gutter-inner;
  }
}

/* scss-combine:account/pages/orders-receipt */
.account-page-orders-receipt .account-content {
  padding-top: $account-gutter-inner;
  border-top: 1px solid $account-border-color;
}

// Summary

.account-receipt-summaries {
  @extend %clearfix;
}

.account-receipt-summary {
  float: left;
  width: 33%;
  padding-right: $account-gutter;
}

.account-receipt-summary-title {
  margin-top: 5px;
  margin-bottom: 10px;
  font-size: $account-font-size;
}

.account-receipt-summary-address {
  line-height: 1.5;
}

.account-receipt-detail {
  margin-bottom: 5px;
}

.account-receipt-detail-title {
  color: $account-text-color-light;
}

// Comments

.account-receipt-comments {
  white-space: pre-line;
}

// Receipt Items (products)

.account-receipt-items {
  padding-top: $account-gutter-inner;
}

.account-receipt-download {
  margin: 10px 0;
}

// Totals

.account-receipt-totals {
  display: table;
  width: 50%;
  margin-left: 50%;
}

.account-receipt-total {
  display: table-row;
  margin-bottom: $account-gutter-inner;
}

.account-receipt-total-title,
.account-receipt-total-value {
  display: table-cell;
  padding-top: $account-gutter-inner;
  padding-bottom: $account-gutter-inner;
  border-top: 1px solid $account-border-color;

  .account-receipt-total:first-of-type & {
    border-top: 0;
  }
}

.account-receipt-total-title {
  font-weight: normal;
  letter-spacing: 1px;
  color: $account-text-color-light;
}

.account-receipt-total-value {
  text-align: right;
}

/* scss-combine:account/pages/recent-items */

/* scss-combine:account/pages/returns */
// Return form

.account-return-table {
  th {
    text-align: left;

    &:last-of-type {
      text-align: right;
    }
  }

  .form-field {
    margin-bottom: 0;
  }
}

.account-return-fields {
  margin-top: $account-gutter;

  .form-field-select {
    width: 50%;
  }
}

/* scss-combine:account/pages/wishlists */
.account-wishlists {
  margin-bottom: 30px;
}

/* scss-combine:account/pages/messages */
// Listing

.account-messages-table.table-clean {
  border-bottom: 1px solid $account-border-color;

  td {
    padding-right: 0;
    padding-left: 0;
    vertical-align: top;
    border-top: 1px solid $account-border-color;
  }
}

.account-messages-table-date {
  width: 22%;
  text-align: right;
}

.account-message-subject {
  margin-top: 0;
  margin-bottom: 10px;
  font-size: $account-font-size-heading;
  font-weight: 500;

  .account-message-is-reply &:before {
    padding-right: 5px;
    content: "\21c9";
  }

  .account-message-is-read & {
    color: $account-text-color-light;
  }
}

.account-message-content {
  padding-right: $account-gutter;
  font-size: $account-font-size;
  line-height: 1.5;
  color: $account-text-color;
  white-space: pre-line;

  .account-message-is-read & {
    color: lighten($account-text-color-light,10%);
  }
}

.account-message-date {
  color: $account-text-color-light;
}

// Form

.account-messages-form-subject {
  width: 50%;
}

/* scss-combine:account/pages/download */
// List

.account-download {
  padding: $account-gutter-inner 0 0;
  margin: $account-gutter-inner 0;
  border-top: 1px solid $account-border-color;
}

.account-download-title {
  margin: 0;
  font-size: 1em;
}

.account-download-link {
  color: inherit;
  text-decoration: underline;
}

.account-download-filename {
  font-size: 1.2em;
}

.account-download-filesize {
  padding-left: 5px;
  font-weight: normal;
  color: $account-text-color-light;
}

.account-download-description {
  padding-top: 10px;
  padding-bottom: 10px;
}

.account-download-expiry {
  font-size: 0.9em;
  color: $account-text-color-light;
}

.account-download-expiry-item {
  display: block;
  padding-bottom: 5px;

  &:last-of-type {
    padding-bottom: 0;
  }
}

// Gallery

.account-downloads-gallery-image {
  text-align: center;
}

.account-downloads-gallery-thumbs {
  @extend %clearfix;
  text-align: center;
}

.account-downloads-gallery-thumb {
  display: inline-block;
  width: 10%;
  height: 10%;
  padding: 0 2px;
  vertical-align: middle;
  cursor: pointer;

  img {
    width: 100%;
    height: auto;
  }
}

/* scss-combine:account/pages/invoice */
.account-invoice-date {
  margin-top: -20px;
  font-size: $account-font-size;
}

// Address

.account-invoice-addresses {
  @extend %clearfix;
  margin-top: $account-gutter;
  margin-bottom: $account-gutter;
}

.account-invoice-address {
  float: left;
  margin-right: $account-gutter;
}

.account-invoice-address-title {
  margin-top: 5px;
  margin-bottom: 10px;
  font-size: $account-font-size;
}

.account-invoice-address-body {
  line-height: 1.5;
}

// List

.account-invoice-table {
  .account-wrapper & {
    margin-bottom: 0;
  }

  th,
  td {
    padding-right: $account-gutter-inner;
    text-align: left;
    vertical-align: top;

    &:nth-of-type(0n+3) {
      width: 10%;
    }

    &:last-of-type {
      width: 160px;
      padding-right: 0;
      text-align: right;
    }
  }

  td:first-of-type {
    width: 60px + 25px;
  }
}

.account-invoice-item-thumbnail {
  width: 60px;
  height: auto;
}

.account-invoice-item-name {
  margin-bottom: 5px;
  font-weight: bold;
}

.account-invoice-total-title {
  font-weight: bold;
}

// Totals

.account-invoice-totals {
  .account-wrapper & {
    width: auto;
    margin-top: 0;
    margin-left: auto;
  }

  th,
  td {
    padding-right: $account-gutter-inner;

    &:last-of-type {
      width: 160px;
      padding-right: 0;
      text-align: right;
    }
  }
}

/* scss-combine:account/pages/settings */
.account-page-settings {
  .alert:first-of-type {
    margin-top: 0;
  }

  .account-content {
    padding-top: $account-gutter-inner;
    border-top: 1px solid $account-border-color;
  }
}

.account-settings-form {
  @extend %clearfix;
  padding-top: $account-gutter-inner;

  .form-field {
    float: left;
    width: 50%;
    padding-right: $account-gutter-inner / 2;
    vertical-align: top;

    &:nth-of-type(2n+0) {
      padding-right: 0;
      padding-left: $account-gutter-inner / 2;
    }
  }

  .form-actions {
    padding-top: $account-gutter-inner;
  }
}


/* scss-combine:account/pages/auth-login */

.account-page-login .alert {
  max-width: 860px;
  margin-top: 0;
  margin-right: auto;
  margin-bottom: $account-gutter;
  margin-left: auto;
}

.account-login-wrapper {
  max-width: 860px;
  margin-right: auto;
  margin-left: auto;

  @media screen and (min-width: $account-breakpoint-small) {
    display: table;
    width: 100%;
    table-layout: fixed;
  }
}

.account-page-login .account-sub-heading {
  margin: 0 0 20px;
}

.account-login,
.account-create {
  @media screen and (min-width: $account-breakpoint-small) {
    display: table-cell;
    vertical-align: top;
  }
}

// Login Form

.account-login {
  @media screen and (max-width: $account-breakpoint-small - 1) {
    margin-bottom: $account-gutter;
  }

  @media screen and (min-width: $account-breakpoint-small) {
    padding: $account-gutter-inner (30px + $account-gutter-inner) $account-gutter-inner 0;
  }
}

.account-login-button {
  margin-right: $account-gutter-inner;
}

// New Account

.account-create {
  padding: $account-gutter-inner;
  background: $account-card-background-color;
}

.account-create-intro {
  &:empty {
    display: none;
  }
}

.account-create-facts {
  padding-left: 20px;
  margin-bottom: 30px;
  list-style: disc;

  li:empty {
    display: none;
  }
}

/* scss-combine:account/pages/auth-forgot */
.account-page-forgot .account-body {
  width: 100%;
  max-width: 600px;
  margin-right: auto;
  margin-left: auto;
  text-align: center;
  float: none;
}

.account-forgot-form {
  display: table;
  width: 100%;
  margin-top: $account-gutter;

  .form-field,
  .form-actions {
    display: table-cell;
    text-align: left;
    vertical-align: bottom;
  }

  .form-actions {
    width: 1px;
    padding-left: 10px;
  }
}

/* scss-combine:account/pages/auth-create */
.account-page-create {
  .account-header {
    padding-bottom: $account-gutter-inner;
    margin-bottom: $account-gutter;
    border-bottom: 1px solid $account-border-color;
  }

  .account-body {
    max-width: 860px;
    margin-right: auto;
    margin-left: auto;
  }
}

.account-create-form {
  .g-recaptcha > div {
    margin: 0 auto;
  }

  @media screen and (min-width: $account-breakpoint-small) {
    .form-field {
      float: left;
      width: 50%;
      padding-right: $account-gutter-inner;

      &:nth-of-type(2n) {
        padding-right: 0;
        padding-left: $account-gutter-inner;
      }

      &:nth-of-type(2n + 1) {
        clear: left;
      }
    }
  }
}

.account-create-details,
.account-create-address {
  @extend %clearfix;
  padding-bottom: $account-gutter-inner;
  margin-bottom: $account-gutter;
  border-bottom: 1px solid $account-border-color;
}

.account-create-form-actions {
  text-align: center;
}

.account-create-captcha {
  display: inline-block;
  vertical-align: middle;

  @media screen and (max-width: $account-breakpoint-small - 1) {
    margin-bottom: $account-gutter-inner;
  }

  @media screen and (min-width: $account-breakpoint-small) {
    margin-right: $account-gutter-inner;
  }
}

/* scss-combine:account/pages/auth-created */
.account-page-created .account-body {
  float: none;
  width: 100%;
  max-width: 600px;
  margin-right: auto;
  margin-left: auto;
  text-align: center;

  p {
    margin-bottom: $account-gutter;
    line-height: 1.5;
  }

  em {
    font-family: monospace;
    font-style: normal;
    font-weight: bold;
    background: $account-card-background-color;
  }
}

/* scss-combine:account/pages/auth-new-password */
.account-page-new-password .account-content {
  width: 100%;
  max-width: 400px;
  margin-right: auto;
  margin-left: auto;
}

.new-password-requirements {
  margin-bottom: $account-gutter;
  text-align: center;
}


/* scss-combine:account/pages/gift-certificate */
// (optional) gift card nav

@if ($giftcard-nav-use-default-styles) {
  .giftcard-nav {
    padding: $account-gutter * 2;
    margin-bottom: $account-gutter * 2;
    text-align: center;
  }

  .giftcard-nav-item {
    display: inline-block;
    margin: 0 1em;
    color: $account-link-color;

    &.is-active { color: $account-text-color; }
  }
}

.giftcard-heading-container {
  margin-bottom: $account-gutter * 2;
  text-align: center;
}


// Purchase

.giftcard-form-help-message {
  display: block;
  margin-top: 6px;
}

.giftcard-form-preview {
  iframe { width: 100%; }
}

.giftcard-single-theme {
  display: none;
}


// Check balance

.giftcard-balance-container {
  max-width: 600px;
  margin: 0 auto;
}

.form-giftcard-balance {
  position: relative;

  .form-field-title {
    position: absolute;
    left: -9999px;
  }

  .form-field-control {
    display: block;

    @media screen and (min-width: 600px + ($account-gutter-inner * 2)) {
      margin-right: calc(30% + #{$account-gutter-inner});
    }
  }

  .form-actions {
    @media screen and (min-width: 600px + ($account-gutter-inner * 2)) {
      position: absolute;
      top: 0;
      right: 0;
      width: 30%;
    }
  }

  .button {
    width: 100%;
  }
}


// Redeem

.giftcard-redeem-steps {
  line-height: 1.5;

  li { margin-bottom: 0.75em; }
}



// Module overrides
/* scss-combine:modules/dropdown */
.dropdown {
  position: relative;
}

.dropdown-panel {
  position: absolute;
  top: 100%;
  left: 0;
  z-index: $navbar-z;
  display: none;
  min-width: 100%;
  padding: rem-calc(10);

  &.animating,
  &.visible {
    display: block;
  }

  &.animating-in,
  &.animating-out {
    opacity: 0;
    transform: translateY(-10px);
    transition: opacity 0.5s ease, transform 0.3s ease;
  }

  &.visible {
    opacity: 1;
    transform: translateY(0);
  }
}

.icon-arrow-down {
  display: inline-block;
  margin-right: rem-calc(3);
  margin-left: 0.5em;
  vertical-align: middle;
  transition: transform 0.3s ease;

  .dropdown-open & {
    transform: rotate(-180deg);
  }
}

.submenu-dropdown-panel {
  margin-left: 15px;
}

/* scss-combine:modules/modal */
.modal-content-wrap {
  display: none;
}

.modal-overlay {
  background-color: rgba($color-modal-background, 0.95);

  @if (lightness($color-modal-background) > 20) {
    background-color: rgba($color-modal-background, 0.9);
  }
}

.modal {
  margin-bottom: 5vw;
  width: 90%;
  max-width: $modal-width;

  .modal-content {
    padding: rem-calc(40 20);
    background-color: #fff;

    @include media($min: $breakpoint-s) {
      padding: rem-calc(72);
    }
  }

  &.modal-quick-shop {
    top: 5vw;
  }

  .icon-spinner {
    display: block;
    width: 30px;
    height: 30px;
    margin: 0 auto;
  }

  .form-actions {
    text-align: center;

    .button {
      min-width: rem-calc(174);
    }
  }

  .button + .button {
    margin-left: 1.5em;
  }
}

.modal-narrow {
  max-width: $container-width-narrow;

  .modal-content {
    @include media($min: $breakpoint-s) {
      padding: rem-calc(102);
    }
  }
}

.button-modal-close {
  position: fixed;
  top: 19px;
  right: 20px;
  color: contrast($color-modal-background, 100%);
  background: none;

  .icon-close {
    width: 16px;
    height: 16px;
  }
}

.modal-close {
  position: absolute;
  top: rem-calc(28);
  right: rem-calc(28);padding: 0;
  background-color: transparent;

  .icon-close {
    width: rem-calc(13);
    height: rem-calc(13);
  }
}

.modal-header {
  text-align: center;

  .title {
    margin: 0;
  }

  .subtitle {
    @extend %h6;
    margin: rem-calc(12 0 45);
  }
}

/* scss-combine:modules/tabs */
.tabs {
  border-top: 1px solid $color-border;
  border-bottom: 1px solid $color-border;
}

.tab-title,
.accordion-title {
  padding: 0;
  margin-right: rem-calc(60);
  letter-spacing: 0.09375em;
  text-transform: uppercase;

  &.active {
    position: relative;
    font-weight: 400;
    color: $color-heading;

    &:before {
      position: absolute;
      top: -1px;
      left: 0;
      display: block;
      width: 100%;
      border-top: 2px solid $color-accent;
      content: "";
    }
  }

  a {
    display: block;
    padding: rem-calc(20 0);
    color: inherit;
  }
}

.accordion-title {
  display: block;
  margin-right: 0;
  border-top: 1px solid $color-border;

  @include media($min: $breakpoint-s) {
    display: none;
  }

  &:last-of-type {
    border-bottom: 1px solid $color-border;
  }

  &.is-open {
    color: $color-heading;

    .icon-chevron-down {
      transform: rotate(180deg);
    }
  }

  .icon-chevron-down {
    width: rem-calc(10);
    height: rem-calc(16);
    float: right;
    margin-top: rem-calc(4);
  }
}

.tabs-content {
  position: relative;
  min-height: rem-calc(60);

  .icon-spinner {
    position: absolute;
    top: rem-calc(30);
    left: 50%;
    margin-left: -10px;
  }
}

.tab-content-panel {
  &.active ~ .icon-spinner {
    display: none;
  }
}


// General
/* scss-combine:general/general */
/* General */

body {
  @extend %body-text-treatment;
  background-color: $color-background;
  font-family: $font-primary;

  &.scroll-locked,
  &.scroll-locked-loading { overflow: hidden; }
}

/* Links */

a {
  color: inherit;
  transition: color 0.2s $ease-out-quad;
}

.link {
  @extend %link-style;
}

/* Headers */

h1,
h2,
h3,
h4,
h5,
h6 {
  color: $color-heading;

  a {
    color: inherit;
    text-decoration: none;

    &:hover {
      color: $color-accent;
    }
  }
}

h1 { @extend %h1; }
h2 { @extend %h2; }
h3 { @extend %h3; }
h4 { @extend %h4; }
h5 { @extend %h5; }
h6 { @extend %h6; }

/* Utility classes */

.text-center {
  text-align: center;
}

.sr-only {
  @extend .show-for-sr;
}

.hidden {
  display: none;
}

/* Basic elements */

hr {
  background: $color-border;
  height: 1px;
}

cite {
  color: $color-meta;
}

img {
  max-width: 100%;
  height: auto;

  // Hide lazy-loading images while they are loading
  &:not([src]) {
    visibility: hidden;
  }
}

svg {
  fill: currentColor;
}

.list-bulleted {
  margin: 1em 0 1em 1.5em;
  list-style: disc;
}

/* scss-combine:general/button */
/* Buttons */
button {
  border: 0;
}

.button {
  @extend %button-primary-style;

  &:hover {
    color: $color-button-background;
    background-color: $color-button;
    box-shadow: 0 0 0 1px $color-button-background inset;
  }

  &.button-small {
    padding: 0.25em 0.5em;
    font-size: 13px;
  }

  &.button-short {
    padding: rem-calc(18 20 17);
  }

  &.button-secondary {
    @extend %button-secondary-style;

    &:hover {
      color: contrast($color-button-secondary, 100%);
      background-color: $color-button-secondary;
      box-shadow: 0 0 0 1px $color-button-secondary inset;
    }

    &.inverted {
      color: contrast($color-button-secondary, 100%);
      box-shadow: 0 0 0 1px contrast($color-button-secondary, 100%) inset;

      &:hover {
        background-color: contrast($color-button-secondary, 100%);
        color: $color-button-secondary;
      }
    }
  }

  &:disabled,
  &.button-disabled {
    cursor: not-allowed;
    opacity: 0.5;
  }

  &.spinner {
    position: relative;

    &.progress {
      .button-text {
        opacity: 0.1;
      }
    }
  }

  .icon-spinner,
  .icon-check-mark {
    position: absolute;
    top: 50%;
    left: 50%;
    width: 20px;
    height: 20px;
    margin: -10px 0 0 -10px;
  }
}

.button-group {
  @extend %clearfix;
  display: inline-block;
}

.button-group-item {
  position: relative;
  float: left;
  height: 45px;
  min-width: 45px;
  padding: 12px;
  font-family: $font-secondary;
  font-size: rem-calc(13px);
  line-height: 1;
  color: $color-meta;
  text-align: center;
  background-color: transparent;
  border: 2px solid $color-border;
  transition: color 0.3s ease;

  &:hover {
    z-index: 1;
    color: $color-accent;
    border-color: $color-accent;
  }

  &:first-child {
    border-radius: $border-radius 0 0 $border-radius;
  }

  &:last-child {
    border-radius: 0 $border-radius $border-radius 0;
  }

  &:not(:first-child) {
    margin-left: -2px;
  }

  &.active {
    color: contrast($color-nav-active, 100%);
    background-color: $color-nav-active;
    border-color: $color-nav-active;
  }

  &.disabled,
  &.active {
    @extend %disable-pointer-events;
  }

  .icon-grid {
    width: rem-calc(21);
    height: rem-calc(21);
  }

  .icon-list {
    width: rem-calc(28);
    height: rem-calc(21);
  }
}

/* scss-combine:general/form */
/*----------  Forms  ----------*/

.form-field {
  margin-bottom: rem-calc(28px);
}

.form-input {
  display: block;
  padding: rem-calc(22 20);
  font-size: rem-calc(12);
  letter-spacing: 0.125em;
  color: $color-input;
  border: 1px solid $color-border;
  transition: border-color 0.2s $ease-out-quad;

  @if $font-primary == "Roboto Slab" {
    font-family: $font-secondary;
  }

  &::placeholder {
    @extend %placeholder-text-treatment;
  }

  &:focus {
    outline: none;
  }

  &.form-input-short {
    padding: rem-calc(17);
  }

  &[type="text"],
  &[type="password"],
  &[type="email"],
  &[type="file"] {
    height: rem-calc(60); // Fix height since different fonts cause different heights
    background: transparent;
  }

  // IE style overrides
  &::-ms-value { background: transparent; }

  &::-ms-browse {
    background: transparent;
    border: 1px solid $color-border;
    border-radius: 4px;
  }
}

.form-radio,
.form-checkbox {
  display: inline-block;
  vertical-align: middle;
  margin-right: rem-calc(5);
}

.form-select-wrapper {
  position: relative;
  display: block;

  &:after {
    @include icon-arrow-down($color-meta);
    position: absolute;
    top: 50%;
    right: 15px;
    display: inline-block;
    width: 9px;
    height: 6px;
    margin-top: -3px;
    content: "";
    pointer-events: none;
  }

  .form-select { width: 100%; }
}

.form-select {
  height: rem-calc(60);
  padding: 0 rem-calc(22) 0 rem-calc(17);
  background: transparent;
  appearance: none;

  // For IE 10+
  &::-ms-expand { display: none; }

  &.form-input-short {
    height: rem-calc(50);
    padding: 0 rem-calc(26) 0 rem-calc(17);
  }
}

.form-field-title {
  @extend %label-text-treatment;
  margin: 0 0 rem-calc(8);
}

.form-label {
  &.checkbox,
  &.radio {
    .form-label-text {
      margin: 0 0 0 rem-calc(5);
    }
  }
}

.input-group {
  @include layout-group;
}

.input-group-item {
  @include layout-group-item;
  vertical-align: top;

  &:first-child {
    .button {
      margin-right: -2px;
    }
  }

  &:last-child {
    .button {
      margin-left: -2px;
    }
  }

  &.form-field {
    width: 99%;
  }

  .form-input {
    width: 100%;
  }
}

/*----------  Form Errors  ----------*/

.form-field-invalid {
  .form-input {
    border: 1px solid $color-error;

    &::placeholder {
      color: $color-error;
    }
  }
}

.form-error-message {
  color: $color-error;
}

.search-form {
  position: relative;
  max-width: 250px;
  margin: rem-calc(22px) auto 0;

  .search-input {
    width: 100%;
  }

  .button-search-submit {
    position: absolute;
    top: 50%;
    right: $gutter;
    line-height: 0;
    color: $color-meta;
    background: none;
    transform: translateY(-50%);
  }

  .icon-search {
    width: 16px;
    height: 16px;
  }
}

.form-inline-message {
  display: block;

  .form-field-invalid & {
    color: $color-error;
  }
}

// Hide HTML5 Up and Down arrows in number input[type="number"]
input[type="number"]::-webkit-outer-spin-button,
input[type="number"]::-webkit-inner-spin-button {
    -webkit-appearance: none;
    margin: 0;
}

input[type="number"] {
    -moz-appearance: textfield;
}

/* scss-combine:general/table */
/* Tables */

table {
  color: $color-body;
  border-color: $color-border;
}

th {
  @extend %heading-text-treatment;
  color: $color-input;
}

/* scss-combine:general/alerts */
.alert {
  position: relative;
  padding: rem-calc(16) rem-calc(32) rem-calc(16) rem-calc(16);
  font-size: rem-calc(14);
  color: $color-alert-text;
  text-align: center;

  &.dismissed { opacity: 0; }

  &.hidden,
  &.alert-dynamic { display: none; }

  &.visible {
    display: block;
  }

  .alert-dismiss {
    position: absolute;
    top: 50%;
    right: rem-calc(16);
    font-size: rem-calc(24);
    line-height: 0;
    color: inherit;
    cursor: pointer;

    &:hover { opacity: 1; }
  }

  &.alert-success,
  &.success {
    background-color: $color-success;

    .alert-dismiss { color: darken($color-success, 20%); }
  }

  &.alert-error,
  &.error {
    background-color: $color-error;

    .alert-dismiss { color: darken($color-error, 30%); }
  }

  &.alert-info,
  &.neutral {
    background-color: $color-info;

    .alert-dismiss { color: darken($color-info, 30%); }
  }

  a:not(.alert-dismiss) {
    color: $color-alert-text;
    text-decoration: underline;

    &:hover {
      text-decoration: none;
    }
  }
}

/* scss-combine:general/global */
/*----------  Spinner  ----------*/

.loading-overlay {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 9;
  background-color: rgba($color-background, 0.8);

  .icon-spinner {
    position: fixed;
    top: 50%;
    left: 50%;
    margin: -10px 0 0 -10px;
  }
}

/*----------  Icons  ----------*/

.icon-arrow-down {
  @include icon-arrow-down();
}


/*----------  Animations  ----------*/

.icon-spinner {
  width: 20px;
  height: 20px;
  animation: spin 1s linear infinite;
}

@keyframes spin {
  100% { transform: rotate(360deg); }
}


/*----------  Breadcrumbs  ----------*/

.breadcrumbs {
  margin-top: rem-calc(40px);
  margin-bottom: rem-calc(25px);
  text-align: center;
  font-size: rem-calc(13);

  .page-header & { margin-top: 0; }
}

.breadcrumb {
  display: inline-block;
  font-weight: normal;
  letter-spacing: rem-calc(1.5);

  &:not(:last-child) {
    &:after {
      content: "/";
    }
  }
}

.breadcrumb-link:hover {
  color: $color-accent;
}

.breadcrumb-label {
  &:not(.breadcrumb-link) {
    color: $color-bold;
  }
}


/*----------  Page  ----------*/

.page-header {
  @extend %page-header-treatment;

  .page-title {
    margin: 0 auto;
  }
}

.page-title {
  @extend %h3;
  text-align: center;
}

.section-title {
  @extend %section-title-text-treatment;
  margin: 0 0 rem-calc(41);
  font-size: rem-calc(24);

  .default & {
    margin-bottom: rem-calc(57);
  }
}

.page-content,
.product-description,
.listing-description,
.blog-content {
  ul,
  ol {
    @extend %list-style;
  }

  ul {
    list-style: disc;
  }

  a {
    color: $color-link;

    &:hover {
      color: $color-link-hover;
      text-decoration: underline;
    }
  }

  img {
    margin-bottom: 2em;
  }
}


/*----------  Pagination  ----------*/

.pagination {
  text-align: center;

  .icon {
    width: rem-calc(10);
    height: rem-calc(10);
  }

  .pagination-link {
    position: relative;
    display: inline-block;
    width: rem-calc(32);
    height: rem-calc(32);
    margin: 0 rem-calc(8);
    font-weight: 300;
    line-height: rem-calc(32);

    &:before {
      position: absolute;
      top: 0;
      left: 0;
      z-index: -1;
      display: block;
      width: 100%;
      height: 100%;
      background-color: $color-background;
      border: 1px solid;
      content: "";
      transform: rotate(45deg);
      transition: all 0.2s ease;
    }

    &.active {
      color: $color-bold;
    }

    &.disabled {
      opacity: 0.5;
      cursor: default;
      pointer-events: none;
    }

    &:hover {
      color: contrast($color-accent, 100%, 60);

      &:before {
        background-color: $color-accent;
        border-color: $color-accent;
      }
    }
  }
}


/*----------  Banners  ----------*/

.banners {
  @extend %clearfix;
  padding: 0 $gutter;
}

.banner {
  @extend h4;
  max-width: $max-width;
  padding: 1em 5vw;
  margin: $gutter auto;
  text-align: center;
  background-color: darken($color-background-tertiary, 2.1569);

  p {
    &:first-child {
      margin-top: 0;
    }

    &:last-child {
      margin-bottom: 0;
    }
  }

  @include media($min: $breakpoint-s) {
    padding: 1em 15vw;
  }

  // override default rte colors
  h1,
  h2,
  h3,
  h4,
  h5,
  h6 { color: inherit; }

  a {
    text-decoration: underline;

    &:hover {
      text-decoration: none;
    }
  }
}


/*----------  Quantity widget  ----------*/

.quantity-control {
  position: relative;

  .button-control-action {
    position: absolute;
    width: rem-calc(30);
    height: rem-calc(30);
    line-height: 0;
    background-color: $color-background;
    border: 1px solid $color-border;
    border-radius: 0;
    transition: color 0.3s ease, background-color 0.3s ease;

    &:hover {
      color: $color-background;
      background-color: $color-background-secondary;

      @if (lightness($color-background-secondary) > 60) {
        color: inherit;
      }
    }

    &:focus {
      z-index: 1;
    }

    + .button-control-action {
      bottom: 0;
      border-top: 0;
    }
  }

  .form-input {
    display: inline-block;
    height: rem-calc(60);
    max-width: rem-calc(175);
    font-family: $font-primary;
    border: 1px solid $color-border;
    border-right: 0;
    -moz-appearance: textfield;

    &::-webkit-inner-spin-button,
    &::-webkit-outer-spin-button {
      appearance: none;
    }
  }
}


// Components
/* scss-combine:components/mini-cart */
.mini-cart {
  width: $mini-cart-width;
  color: $color-body;
}

.mini-cart-inner {
  max-height: 80vh;
  overflow: auto;
}

.mini-cart-item {
  position: relative;
  padding: rem-calc(0 20);

  &.removing {
    .mini-cart-item-wrap {
      pointer-events: none;
      opacity: 0.2;
    }
  }

  .icon-spinner {
    position: absolute;
    top: rem-calc(25);
    right: rem-calc(15);
  }
}

.mini-cart-item-wrap {
  @include layout-group();
  padding: $mini-cart-padding 0;
  border-bottom: 1px solid $color-border;

  > * {
    @include layout-group-item();
  }

  .giftcard-image {
    width: 75px;
  }
}

.mini-cart-item-thumb {
  width: rem-calc(75);
  padding-bottom: rem-calc(75);
  background-position: center center;
  background-repeat: no-repeat;
  background-size: contain;

  .thumbnail-cover & {
    background-size: cover;
  }
}

.mini-cart-item-description {
  width: 90%;
  padding: 0 $mini-cart-padding;
  vertical-align: middle;
}

.mini-cart-item-remove {
  vertical-align: top;

  .icon {
    width: rem-calc(8);
    height: rem-calc(8);
  }
}

.mini-cart-item-title {
  @extend h4;
}

.mini-cart-item-price {
  display: block;
  color: $color-bold;
  font-weight: normal;
}

.mini-cart-footer {
  padding: 0 $mini-cart-padding $mini-cart-padding;

  .mini-cart-subtotal {
    float: right;
    font-size: 111%;
    vertical-align: middle;
  }
}

.mini-cart-empty {
  padding-top: $mini-cart-padding;
  margin: 0;
  text-align: center;
}

.mini-cart-footer-title {
  padding: rem-calc(37) 0;
  margin: 0 0 $mini-cart-padding;
  border-bottom: 1px solid $color-border;
}

.mini-cart-actions {
  .button {
    width: calc(50% - 5px);
    padding-left: rem-calc(5);
    padding-right: rem-calc(5);

    + .button { float: right; }
  }
}

/* scss-combine:components/navigation */
$local-navbar-transition: all 0.6s ease;

.main-nav-bar {
  position: relative;
  background-color: $color-nav-background;

  @if ($color-nav-background == $color-background) {
    box-shadow: 0 1px 0 0 contrast($color-nav-background, 5%);
  }

  @include media($max: $breakpoint-mobile-nav) {
    display: none;
  }

  > .container {
    position: relative;
    height: $navbar-height;
    padding: 0;
  }

  .flyout-menu & {
    display: none;
  }

  .navigation {
    position: absolute;
    z-index: $navbar-z;
    left: 50%;
    height: 100%;
    transform: translateX(-50%);
    transition: $local-navbar-transition;

    .sticky & {
      left: 0;
      transform: translateX(0);
    }

    > ul {
      display: inline-block;
      white-space: nowrap;

      > li {
        display: inline-block;
      }
    }

    .dropdown-panel {
      background-color: $color-mega-nav-background;
      border-right: 1px solid $color-border;
      border-bottom: 1px solid $color-border;
      border-left: 1px solid $color-border;
    }

    .dropdown-open {
      background-color: $color-mega-nav-background;
      box-shadow: 0 1px 0 0 $color-border inset, -1px 0 0 0 $color-border inset, 1px 0 0 0 $color-border inset;

      & > a {
        color: $color-nav-active;
      }
    }
  }

  .customer-utils {
    position: relative;
    z-index: 11;
    float: right;
    margin-top: rem-calc(18);
    margin-right: $gutter;
    color: $color-nav;
    visibility: hidden;
    opacity: 0;
    transform: translateY(-80px);
    transition: $local-navbar-transition;

    @include media($max: $breakpoint-l) {
      .currency-selector,
      .customer-links {
        display: none;
      }
    }

    .sticky & {
      visibility: visible;
      opacity: 1;
      transform: translateY(0);
    }
  }

  .currency-selector {
    top: 0;
  }

  .compare-wrap {
    position: relative;

    .compare-tab {
      top: rem-calc(40);

      &:before,
      &:after {
        margin-right: rem-calc(-220);
      }
    }
  }

  .mini-cart {
    top: rem-calc(40);
  }

  .top-level-nav-link {
    display: block;
    padding: rem-calc(19 18);
    font-size: rem-calc(14);
    font-weight: normal;
    color: $color-nav;
    text-transform: uppercase;
    transition: $local-navbar-transition;

    @if ($font-secondary == "Oswald") {
      font-family: $font-secondary;
      letter-spacing: 0.0714em;
    }

    &:hover:not(.items) {
      color: $color-accent;
    }

    &.items {
      span:hover {
        color: $color-accent;
      }
    }
  }
}

.mega-nav {
  position: static;
  transition: background-color $transition-navbar;

  &:after {
    position: relative;
    z-index: $navbar-z + 1;
    display: block;
    width: 100%;
    height: 1px;
    opacity: 0;
    content: "";
    transition: opacity $transition-navbar;

    @if (lightness($color-mega-nav-background) < 50) {
      background-color: $color-mega-nav-background;
    } @else {
      background-color: $color-background;
    }
  }

  &.dropdown-open {
    background-color: $color-mega-nav-background;
    box-shadow: 0 1px 0 0 $color-border inset, 1px 0 0 0 $color-border, 1px 0 0 0 $color-border inset;

    &:after {
      opacity: 1;
    }
  }
}

.mega-nav-panel {
  top: 100%;
  left: 0;
  width: 100vw;
  height: 60vh;
  overflow-y: auto;
  padding: rem-calc(40px) 0;
  background-color: $color-mega-nav-background;
  text-align: center;
  border-top: 1px solid $color-border;
  border-bottom: 1px solid $color-border;
  transition: $local-navbar-transition;

  &.animating-in,
  &.animating-out {
    transform: translateY(0);
  }

  &.variant { overflow: hidden; }

  .sticky & {
    top: $navbar-height;
  }

  .full-width {
    max-width: none;
  }

  &:after {
    content: " ";
    display: block;
    clear: both;
  }
}

.mega-nav-list {
  @include span(5.75);
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  align-items: flex-start;

  .no-images & {
    width: 100%;
  }

  a {
    display: inline-block;
    margin-bottom: 0.75rem;
    color: lighten($color-mega-nav-link, 15%);

    &:hover {
      color: lighten($color-mega-nav-link, 25%);
    }
  }

  .parent {
    display: inline-block;
    margin-right: 1.6rem;
    margin-bottom: 0;
    text-align: left;
    vertical-align: top;

    > a {
      @extend %h5;
      color: $color-mega-nav-link;

      &:hover {
        color: lighten($color-mega-nav-link, 15%);
      }
    }

    &:not(.has-children) {
      a {
        margin-bottom: 0;
      }
    }
  }

  .has-children {
    > a {
      margin-bottom: rem-calc(25);
    }
  }

  .children ul {
    margin-left: 15px;
  }
}

.category-image-container {
  @include span(6 last);
  height: 14vw;
  position: relative;
  background-position: center center;
  background-size: cover;

  .no-images & {
    display: none;
  }
}

.category-image {
  height: 14vw;
  background-position: center center;
  background-size: cover;

  .category-link {
    position: absolute;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: rgba($color-accent, 0.75);
    opacity: 0;
    transition: opacity 0.3s ease-out;

    &:before {
      @extend %ghost-center;
    }

    &:hover,
    &.hover {
      opacity: 1;

      .category-cta {
        transform: translateY(0);
      }
    }
  }
}

.category-cta {
  @extend %h2;
  display: inline-block;
  width: 100%;
  text-align: center;
  color: contrast($color-accent, 100%, 70);
  transform: translateY(#{rem-calc(-20)});
  transition: transform 0.5s ease-out;
}

/* scss-combine:components/navigation-variant */
.navigation-container {
  display: block;
  float: left;
  margin: 0;
  overflow: hidden;
  padding: 10px;
  position: relative;
  width: 100%;

  > .container {
    color: $color-nav-active;
    max-width: rem-calc(1230px);
  }

  &:after {
    clear: both;
    content: ' ';
    display: block;
  }

  li {
    color: $color-mega-nav-link;

    &:hover {
      color: rgba($color-mega-nav-link, 0.6);
    }
  }
}

.navigation-root-container {
  display: block;
  float: left;
  height: 45vh;
  margin-left: rem-calc(20px);
  max-height: 45vh;
  padding-bottom: rem-calc(30px);
  position: relative;
  text-align: left;
  width: span(3);

  &:after {
    border-left: 1px solid contrast($color-mega-nav-background, 10%);
    content: '';
    display: block;
    height: 45vh;
    position: absolute;
    right: 0;
    top: 0;
    width: 0;
  }
}

.navigation-root-list {
  display: block;
  width: 100%;
  max-height: 45vh;
  overflow-y: auto;
  overflow-x: hidden;
  white-space: normal;
}

.navigation-root-item {
  display: block;
  width: 100%;
  font-weight: bolder;
  position: relative;
  padding-right: 20px;

  &.active {
    position: relative;

    &::after {
      position: absolute;
      top: 50%;
      left: 0;
      z-index: -1;
      width: 100%;
      display: block;
      border-bottom: 1px solid $color-border;
      content: "";
    }

    a {
      padding-right: rem-calc(10);
      background-color: $color-mega-nav-background;
    }
  }

  &:not(:last-child) { margin-bottom: rem-calc($gutter); }
}

.navigation-child-container {
  position: relative;
  display: block;
  overflow: hidden;
  width: span(8);
  max-height: 45vh;
  float: left;
  margin-left: 20px;
}

.navigation-child-list-container {
  display: none;
  position: absolute;
  top: 0;

  &.animating,
  &.visible {
    display: block;
    padding-left: $gutter;
    max-height: 45vh;
    overflow-y: hidden;
    overflow-x: auto;
    text-align: left;
  }

  &.animating-in {
    opacity: 0;
    transform: translateY(-20px);
    transition: opacity 0.3s ease, transform 0.3s ease;
  }

  &.animating-out {
    opacity: 0;
  }

  &.visible {
    position: static;
    opacity: 1;
    transform: translateY(0);
  }
}

.navigation-child-list-header {
  display: block;
  width: 100%;
  color: contrast($color-mega-nav-background, 100%);
  font-family: $font-primary;
  font-weight: normal;
  font-size: 1.5em;
  text-align: left;
  padding-left: 5px;
  padding-top: 0;
  margin-top: 0;
  margin-bottom: rem-calc(5px);
}

.navigation-child-list-inner {
  display: flex;
}

.navigation-child-list {
  display: flex;
  height: 35vh;
  text-align: left;
  flex-wrap: wrap;
  flex-direction: column;

  .navigation-child-item {
    padding-left: 0;
    padding: 5px;
    margin-right: 40px;
    align-self: baseline;

    .child-item-arrow svg {
      margin-top: -2px;
      transform: rotate(-90deg);
    }
  }
}

.child-list-back {
  display: inline-block;
  font-size: 12px;
  margin: 0;
  margin-bottom: 10px;
  padding: 5px 5px 5px 0;
  text-transform: uppercase;

  &:hover {
    a { color: $color-accent; }

    svg { color: $color-accent; }
  }

  .icon-arrow-down {
    margin-top: -2px;
    transform: rotate(90deg);
  }

  a { text-decoration: underline; }
}

/* scss-combine:components/navigation-mobile */
.nav-mobile-open {
  .top-bar {
    position: fixed;
    width: 100%;
    color: contrast($color-background-secondary, 100%);
    background-color: $color-background-secondary;
    transition: background-color $transition-navbar;

    .flyout-menu & { z-index: $alert-z + 1; }
  }

  .site-branding {
    color: contrast($color-background-secondary, 100%);
  }

  .button-mobile-nav-toggle .burger {
    background-color: contrast($color-background-secondary, 100%);
    transform: rotate(-45deg);

    &:before { transform: rotate(90deg); }
    &:after { display: none; }
  }
}

.button-mobile-nav-toggle {
  position: absolute;
  top: 50%;
  right: rem-calc(10);
  height: rem-calc(20);
  transform: translateY(-50%);

  .logo-align-right & {
    left: rem-calc(10);
    z-index: $navbar-z;
  }

  @include media($min: $breakpoint-mobile-nav) {
    display: none;

    .flyout-menu & { display: block; }
  }

  &:focus { outline: none; }

  .burger {
    display: block;
    width: rem-calc(20);
    height: rem-calc(2);
    background-color: contrast($color-topbar-background, 100%);
    transition: transform 0.2s ease;

    &:before,
    &:after {
      display: block;
      width: rem-calc(20);
      height: rem-calc(2);
      background-color: inherit;
      content: "";
      transition: transform 0.2s ease;
    }

    &:before {
      transform: translateY(rem-calc(-6));
    }

    &:after {
      transform: translateY(rem-calc(4));
    }
  }
}

.navigation-mobile {
  position: fixed;
  z-index: $navbar-z - 1;
  top: $topbar-height;
  width: 100%;
  height: calc(100vh - #{$topbar-height});
  display: none;
  overflow-y: auto;
  background-color: $color-background-secondary;
  -webkit-overflow-scrolling: touch;

  .flyout-menu & { z-index: $alert-z + 1; }

  &.animating,
  &.visible {
    display: block;
  }

  &.animating-in,
  &.animating-out {
    transform: translateY(-100%);
    transition: transform 0.3s ease;
  }

  &.visible {
    transform: translateY(0);
    opacity: 1;
  }

  button {
    background: none;
  }

  .utils-wrap {
    position: relative;
  }

  .utils {
    position: relative;
    display: flex;
    padding-top: rem-calc(15);
    padding-bottom: rem-calc(15);
    color: contrast($color-background-secondary, 100%);
    background-color: $color-utils-mobile-background;

    @if ($color-topbar-background == $color-nav-background) {
      box-shadow: 0 1px 0 0 rgba(contrast($color-nav-background, 100%), 0.1);
    }

    .dropdown {
      position: static;
    }

    .dropdown-panel-tooltip {
      top: 100%;
      right: $gutter;
      width: calc(100vw - #{$double-gutter});
      min-width: 0;
      padding: $gutter;
      color: $color-body;
    }
  }

  .util {
    flex: 1 1 auto;
    height: rem-calc(30);
    line-height: rem-calc(30);
    text-align: center;
    border-right: 1px solid rgba(contrast($color-background-secondary, 100%), 0.1);

    &:last-child {
      border: 0;
    }
  }

  .currency-selector {
    top: 0;
    margin: 0;
  }

  .cart-link {
    position: relative;
  }

  .icon {
    display: inline-block;
    vertical-align: middle;
  }

  .icon-heart {
    width: rem-calc(18);
    height: rem-calc(14);
  }

  .search-form {
    position: relative;
    display: block;
    margin: 0;
    max-width: none;

    .form-field {
      margin: 0;
    }
  }

  .button-search-toggle {
    padding-top: 0;
  }

  .icon-search {
    width: rem-calc(18);
    height: rem-calc(18);
  }

  .search-input {
    @extend .form-input;
    width: 100%;
  }

  .button-search-submit {
    position: absolute;
    top: 50%;
    right: $gutter;
    padding: 0;
    background: none;
    transform: translateY(-50%);

    .icon-search {
      width: rem-calc(16);
      height: rem-calc(16);
    }
  }

  .dropdown-account {
    .dropdown-panel:before,
    .dropdown-panel:after {
      right: calc(62.5% - #{rem-calc(8)});
    }
  }

  .dropdown-search {
    .dropdown-panel:before,
    .dropdown-panel:after {
      right: calc(12.5% - #{rem-calc(22)});
    }

    .dropdown-panel {
      margin-bottom: rem-calc(15);
    }
  }

  .account-link {
    @include gallery(6);
    display: inline-block;
    text-align: center;
  }

  .navigation {
    padding: rem-calc(20);
    margin-top: $gutter;

    li,
    a { display: block; }

    a {
      padding: rem-calc(8) 0;
    }

    .parent > a { color: $color-background-secondary-inverse; }

    .children a { color: rgba($color-background-secondary-inverse, 0.75); }

    .dropdown-panel {
      position: static;
      padding: 0;
    }

    .icon-chevron-down {
      float: right;
      margin-top: rem-calc(2);
      margin-right: calc(12.5% - #{rem-calc(20)});
      width: rem-calc(10);
      height: rem-calc(16);
      transition: transform 0.3s ease;

      @include media($min: $breakpoint-xs) {
        float: none;
        margin-top: 0;
        margin-left: 1rem;
      }
    }

    .dropdown-open .icon-chevron-down {
      transform: rotate(180deg);
    }
  }

  .top-level-nav-link {
    font-size: rem-calc(18);
    color: contrast($color-background-secondary, 100%);
    text-transform: uppercase;

    + .dropdown-panel {
      padding-bottom: $gutter;
      margin-top: $gutter;
      margin-bottom: $gutter;
      border-bottom: 1px solid rgba($color-background-secondary-inverse, 0.1);
    }
  }

  .children {
    padding-left: $gutter;

    ul { margin-left: $gutter; }
  }
}

.mobile-compare-link {
  position: absolute;
  top: 50%;
  left: 0;
  display: none;
  padding-left: 0.625rem;
  font-size: rem-calc(10);
  color: inherit;
  transform: translateY(-50%);

  .category &,
  .brand &,
  .search & {
    width: auto;
  }

