/*----------  Listing Grid  ----------*/

.product-listing-grid {
  @include media($min: $breakpoint-s) {
    @include span(9);
    @include last;
  }

  .no-sidebar & {
    @include media($min: $breakpoint-s) {
      @include span(12);
      @include last;
    }
