/* scss-combine:components/products/product-listing-sidebar */
/*----------  Sidebar  ----------*/

.listing-sidebar {
  @include media($min: $breakpoint-s) {
    @include span(3);
  }
}

.faceted-search-toggle {
  display: block;
  border: none;
  cursor: pointer;
  margin-bottom: 3em;

  @include media($min: $breakpoint-s) {
    display: none;
  }

  &:before {
    content: attr(data-show-text);
  }

  &.is-open:before {
    content: attr(data-hide-text);
  }
}

.faceted-search {
  @include media($max: $breakpoint-s) {
    display: none;
  }

  &.is-open {
    display: block;
  }
}

.facet-list {
  margin-bottom: 3em;
}

.facet-list-items {
  display: none;

  &.is-open {
    display: block;
  }

  .product-item-rating {
    position: relative;

    > .icon-star {
      cursor: not-allowed;
    }

    .icon-star {
      width: rem-calc(18);
      height: rem-calc(18);
      pointer-events: none;
    }
  }

  .selected-rating {
    vertical-align: middle;
    white-space: nowrap;
  }

  .product-item-rating-facet {
    display: inline-block;

    &:after {
      position: absolute;
      bottom: 100%;
      left: 0;
      font-size: 12px;
      content: attr(data-rating);
      opacity: 0;
      visibility: hidden;
    }

    &:hover {
      color: $color-accent;

      &:after {
        opacity: 1;
        visibility: visible;
      }

      .icon-star {
        fill: currentColor;
      }
    }

    &:last-of-type {
      margin-right: rem-calc(23);
    }

    &.full,
    &.full-hover {
      color: $color-accent;

      .icon-star {
        fill: currentColor;
      }
    }
  }
}

.facet-list-title {
  @extend %h5;
  margin-bottom: rem-calc(25);
  cursor: pointer;
}

.facet-list-toggle {
