    }

    .button {
      width: 100%;
      padding-right: rem-calc(5);
      padding-left: rem-calc(5);
    }
  }

  .form-field {
    @include layout-group-item;
    width: 33.333%;

    @include media($min: $breakpoint-s, $max: $breakpoint-m) {
      display: inline-block;
      width: 47%;
      margin-bottom: 0;

      &:first-child {
        margin-right: 6%;
      }
    }

    .form-input {
