/* scss-combine:components/products/product-options */

.product-options-container {
  padding-top: $double-gutter;
  border-top: 1px solid $color-border;

  .form-field {
    margin-bottom: 22px;

    &.#{$all-text-inputs} {
      width: 100%;
    }

    input[type="number"] { max-width: 25%; }

    .input-select { width: 100%; }

    textarea {
      height: auto;
      resize: vertical;
    }
  }

  .form-field-title {
    @extend %label-text-treatment;
    display: block;
    margin: 0 0 rem-calc(8px);
  }

  .form-select-wrapper {
    display: inline-block;
    margin-left: 0;
  }

  .required {
