}

.product-option-title {
  @extend %label-text-treatment;
  display: block;
  margin: 0 0 rem-calc(8px);
}

/* file */
.form-file-size,
.form-file-types {
  display: block;
  margin: rem-calc(5) 0;
  font-size: rem-calc(12);
}

/* form feild rectangle */

.form-field-rectangle {
  @extend %clearfix;

  .rectangle {
    position: relative;
    display: inline-block;
    float: left;
    margin: rem-calc(0 10 10 0);
  }

  .rectangle-text {
    display: block;
    height: rem-calc(45);
    min-width: rem-calc(45);
    padding-right: rem-calc(4);
    padding-left: rem-calc(4);
    font-size: rem-calc(12);
    line-height: rem-calc(45);
    text-align: center;
    background-color: $color-background;
    border: 1px solid $color-border;
    transition: all 0.3s ease;
  }

  input[type="radio"] {
    position: absolute;
    z-index: 1;
    width: 100%;
    height: 100%;
    cursor: pointer;
    opacity: 0;

    &:checked + .rectangle-text {
      color: $color-body;
      border-color: $color-body;
    }
  }
}

/* colour swatch */
.form-field-swatch {
  .form-field-control {
    @extend %clearfix;
    clear: left;
  }

  .form-label-text {
    @extend .show-for-sr;
  }

  .swatch-wrap {
    position: relative;
    display: block;
    float: left;
    margin: 0 10px 10px 0;
  }

  // The block the swatch sits within
  .swatch {
    position: relative;
    display: block;
    width: 45px;
    height: 45px;
    overflow: hidden;
    border: 1px solid transparent;
  }

  .swatch-color {
    @extend %fill-parent;
    background-color: #eee;
    background-size: cover;

    &.secondary {
      left: 50%;
    }

    &.tertiary {
      top: 50%;
      left: 50%;
    }
  }

  .swatch-radio {
    position: absolute;
    z-index: 1;
    width: 100%;
    height: 100%;
    cursor: pointer;
    opacity: 0;

    // Selected Swatch Items
    &:checked + .swatch {
      &::after {
        position: absolute;
        top: 50%;
        left: 50%;
        z-index: 1;
        width: 13px;
        height: 10px;
        background-image: url("data:image/svg+xml,%3Csvg width='14' height='10' viewBox='0 0 14 10' xmlns='http://www.w3.org/2000/svg'%3E%3Ctitle%3E6269C699-FBB7-4109-B1F3-15F8609CEA35%3C/title%3E%3Cpath d='M5.72 9.6325l-.3995.3995L.082 4.7936l2.1064-2.1063L5.62 6.1187 11.7704-.032l2.1064 2.1064L6.0192 9.932 5.72 9.6324z' fill='%23fff' fill-rule='evenodd'/%3E%3C/svg%3E");
        content: "";
        transform: translate(-50%, -50%) scale(1);
      }
    }
  }
}

.swatch-pattern-expanded {
  display: none;
  position: absolute;
  bottom: calc(100% + 10px);
  left: 50%;
  z-index: 2;
  padding: 3px;
  border: 1px solid $color-border;
  background: $color-background;
  transform: translateX(-50%);

  .swatch-pattern-image {
    display: block;
    width: 100px;
    height: 100px;
    max-width: none;
    background-size: cover;
  }
}

.swatch-wrap:hover .swatch-pattern-expanded {
  display: block;
}

/* date input */

.date-select-group,
.form-field-date .form-field-control {
  display: flex;

  .form-select-wrapper {
    flex: 1 1 auto;
    margin-left: 0;

    &:nth-child(2) {
      margin-right: 1rem;
      margin-left: 1rem;
    }
  }
}

/* product-picker input type  */

.product-picklist-item {
  position: relative;
  display: block;
  margin-bottom: -1px;
}

.product-picklist-radio {
  position: absolute;
  top: 50%;
  left: 14px;
  margin-top: -7px;
  line-height: 14px;
}

.product-picklist-item-body {
  position: relative;
  display: table;
  width: 100%;
  min-height: 60px;
  padding: 6px 8px 6px 84px;
  font-size: 13px;
  cursor: pointer;
  border: 1px solid transparent;
  border-radius: 3px;

  &.picklist-none { padding-left: 36px; }

  // TODO - set up hover / active styles
  &:hover {
    z-index: 1;
    //border-color: contrast($container-background-color, 6%);
  }

  [type="radio"]:checked ~ & {
    z-index: 2;
    //color: $secondary-color;
    //border-color: $secondary-color;
  }
}

.product-picklist-image-wrap {
  position: absolute;
  top: 8px;
  bottom: 8px;
  left: 34px;
  width: 40px;
  overflow: hidden;
}

.product-picklist-image {
  @include centerer;
}

.product-picklist-label-text {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
}

/* scss-combine:components/products/product-single-tabs */
.product-tabs {
  @extend .container;
  position: relative;

  .share-block {
    margin: $double-gutter 0;

    @include media($min: $breakpoint-m) {
      position: absolute;
      top: rem-calc(22);
      right: rem-calc(15);
      margin: 0;
    }

    &.has-like { display: none; }
  }

  .share-title {
    margin-right: rem-calc(10);
    color: inherit;
    vertical-align: middle;
  }

  .share-buttons-nav {
    display: inline-block;
    vertical-align: middle;
  }

  .tabs {
    @include media($max: $breakpoint-s) {
      display: none;
    }
  }

  .tab-content-panel {
    @extend %clearfix;
    padding-top: rem-calc(45);
    padding-bottom: rem-calc(45);

    @include media($min: $breakpoint-s) {
      border-bottom: 1px solid $color-border;
    }

    :first-child {
      margin-top: 0;
    }

    :last-child {
      @include media($min: $breakpoint-s) {
        margin-bottom: 0;
      }
    }
  }

  .product-description-wrapper {
    @include media($min: $breakpoint-s) {
      width: 50%;
    }
  }


  .video-disabled { display: none; }
}

/*----------  Reviews  ----------*/

.product-reviews-header {
  @extend %clearfix;

  .button {
    @include media($max: $breakpoint-xs) {
      display: block;
      width: 100%;
    }

    @include media($min: $breakpoint-xs) {
      float: right;
    }
  }
}

.product-reviews-title {
  @include media($min: $breakpoint-xs) {
    float: left;
  }
}

.review-items {
  @extend %clearfix;
}

.review-item {
  padding-top: $double-gutter;
  margin-bottom: $double-gutter;

  @include media($min: $breakpoint-s) {
    @include gallery(6);

    &:nth-child(odd) {
      padding-right: $gutter;
    }

    &:nth-child(even) {
      padding-left: $gutter;
    }
  }

  &:first-child {
    border-top: none;
  }

  &.hidden { display: none; }

  &.animating,
  &.visible {
    display: block;
  }

  // initial state for enter transition
  &.animating-in {
    transform: translateY(-20px);
    opacity: 0;
    transition: transform 0.3s ease, opacity 0.3s ease;
  }

  // initial state for leave transition
  &.animating-out {
    opacity: 0;
    transition: opacity 0.2s ease;
  }

  // final states for both in and out
  &.visible {
    transform: translateY(0);
    opacity: 1;
  }

  .product-item-rating {
    float: right;
  }
}

.review-author {
  @extend %h4;
  color: $color-heading;
  text-transform: uppercase;
}

.review-date {
  @extend %h6;
  display: block;
}

.review-item-title {
  margin-bottom: 0;
  font-weight: normal;
  font-size: $font-size-body;
}

.review-item-body {
  margin: 0;
}

.reviews-more-wrap { text-align: center; }

.reviews-show-more-link {
  display: inline-block;
  color: $color-accent;

  &:hover {
    .icon {
      transform: scale(2);
    }
  }

  .icon {
    display: block;
    margin: 11px auto 0;
    width: 9px;
    height: 6px;
    transition: transform 0.3s $ease-out-back;
  }
}

.num-reviews {
  &:before {
    content: attr(data-num-reviews);
  }

  &[data-num-reviews="()"] {
    display: none;
  }
}

#recaptcha_area {
  margin: 0 auto;
}

/*----------  Product Details Block  ----------*/

.product-warranty {
  @include media($min: $breakpoint-s) {
    @include span(5.5);
  }

  & + .product-details-blocks {
    @include media($min: $breakpoint-s) {
      @include span(5.5);
      @include last;
    }
  }

  .product-details-block {
    width: 100%;
  }
}

.product-details-block {
  margin-bottom: 2em;

  @include media($min: $breakpoint-xs) {
    @include gallery(4);
  }
}

.product-details-name {
  @extend %h4;
  color: $color-heading;
}

.product-details-value {
  margin: 0.5em 0 0;
}


/*----------  Review Form  ----------*/

.review-form {
  .form-input {
    width: 100%;
  }

  .rating-name,
  .rating-email,
  .rating-number {
    @include media($min: $breakpoint-s) {
      @include span(5.75);
    }
  }

  .rating-email {
    @include media($min: $breakpoint-s) {
      @include last;
    }
  }

  .rating-subject {
    @include break;
  }

  .rating-stars-label {
    margin-right: rem-calc(10);
    @extend %placeholder;
  }

  .icon-star-wrap {
    display: inline-block;
    transform: translateY(-1px);
  }

  .rating-stars-wrap {
    position: relative;
  }

  .product-item-rating {
    @extend .form-input;
    background-color: #fff;
    pointer-events: none;
    margin-bottom: 0;
  }

  .rating-stars {
    position: absolute;
    top: 0;
    width: 100%;
    height: 100%;
    opacity: 0;
    cursor: pointer;
  }

  .icon-arrow-down {
    position: absolute;
    right: rem-calc(17);
    top: 50%;
    width: 10px;
    height: 6px;
    transform: translateY(-50%);
  }

  .button + .button {
    margin-left: $double-gutter;
  }
}

.review-form-heading {
  margin-top: 0;
  text-align: center;
}

.form-submit-wrap {
  text-align: center;
}


/*----------  Videos  ----------*/

.video-container {
  margin-bottom: rem-calc(45);
}

/* scss-combine:components/apple-pay */
.apple-pay-checkout-button {
  display: none;
  box-sizing: border-box;
  min-width: rem-calc(50);
  height: rem-calc(60);
  background: {
    size: 100% 60%;
    repeat: no-repeat;
    position: 50% 50%;
  }
  border-radius: 5px;

  @if stencilString("style_apple_pay_button") == "white" {
    background-image: -webkit-named-image(apple-pay-logo-black);
    background-color: white;
  }

  @else if stencilString("style_apple_pay_button") == "white-border" {
    background-image: -webkit-named-image(apple-pay-logo-black);
    background-color: white;
    border: 0.5px solid black;
  }

  @else {
    background-image: -webkit-named-image(apple-pay-logo-white);
    background-color: black;
  }

  .additional-checkout-buttons & {
    margin-bottom: rem-calc(15);
  }

  .apple-pay-supported & {
    display: block;
  }
}

/* scss-combine:components/quick-search */
.quick-search {
  @extend .dropdown-panel-tooltip;
  display: none;
  position: absolute;
  width: $mini-cart-width;
  min-height: rem-calc(100);
  max-height: calc(100vh - #{rem-calc(140px)});

  &::before,
  &::after {
    right: rem-calc(16);

    .top-bar.logo-align-left &,
    .top-bar.logo-align-center & {
      right: calc(100% - #{rem-calc(48px)});
    }
  }

  .top-bar & {
    top: 100%;
    left: 0;
  }

  .logo-align-left & {
    left: rem-calc(-31);
  }

  &.animating,
  &.visible {
    display: flex;
  }

  &.animating-in,
  &.animating-out {
    transform: translateY(-10px);
    opacity: 0;
    transition: all 0.3s ease;
  }

  &.visible {
    transform: translateY(0);
    opacity: 1;
  }

  .icon-spinner {
    position: static;
    display: block;
    margin: auto;
  }
}

.quick-search-mobile {
  display: flex;

  .icon-spinner {
    margin: 2rem auto;
  }
}

.quick-search-inner {
  overflow: auto;
  max-height: 100%;
}

.quick-search-results-text {
  @extend %h4;
  text-align: center;
  padding: 1rem;

  .navigation-mobile & {
    text-align: left;
  }
}

.quick-search-list {
  padding: 0 2rem;

  .navigation-mobile & {
    padding: 0 1rem;
  }
}

.quick-search-item {
  padding-top: 1.25rem;
  padding-bottom: 1.25rem;

  &:not(:last-child) {
    border-bottom: 1px solid $color-border;
  }

  &:hover {
    .quick-search-item-title {
      color: $color-accent;
    }
  }
}

.quick-search-item-link {
  display: flex;
  align-items: center;
}

.quick-search-item-image {
  flex: 0 0 rem-calc(75);
  height: rem-calc(75);
  background: {
    size: contain;
    position: 50%;
    repeat: no-repeat;
  }

  .thumbnail-cover & {
    background-size: cover;
  }
}

.quick-search-item-info {
  padding-left: 1.25rem;
}

.quick-search-item-title {
  @extend .mini-cart-item-title;
}

.quick-search-button-wrapper {
  padding: 1.25rem;
  text-align: center;
  border-top: 1px solid $color-border;
}


// Layout
/* scss-combine:layout/layout */
.site-wrapper {
  overflow: hidden;
  width: 100%;

  &.sticky {
    @include media($min: $breakpoint-m) {
      padding-top: $expanded-nav-height;
    }
  }

  &.sticky.flyout-menu { padding-top: 0; }
}

.mini-cart-open {
  .site-header,
  .site-canvas,
  .mini-cart {
    transform: translateX(-$mini-cart-width);
  }
}

.site-canvas {
  position: relative;
  padding-top: $topbar-height;
  transition: transform .3s ease;

  @include media($min: $breakpoint-s) {
    padding-top: $expanded-nav-height;
  }
}

.container {
  @include container($max-width);
  @extend %wrapper-padding;

  &.container-small {
    max-width: 820px;
  }
}

.section {
  @extend %clearfix;
  padding-top: $section-padding;
  padding-bottom: $section-padding;

  .default & {
    padding-top: rem-calc(104);
  }

  &.error {
    text-align: center;
    padding-top: 0;
  }

  .page &,
  .page_contact_form & {
    padding-top: 0;
  }
}

/* scss-combine:layout/header */
.site-header {
  background-color: $color-background;

  .sticky & {
    @include media($min: $breakpoint-mobile-nav) {
      position: fixed;
      top: 0;
      z-index: $navbar-z;
      width: 100%;
    }
  }

  .flyout-menu & {
    position: static;
  }

  .scroll-locked & {
    position: relative;
    z-index: initial;
  }

  .site-alerts {
    position: fixed;
    z-index: $navbar-z;
    bottom: 0;
    width: 100%;
  }
}


/*----------  Top Bar  ----------*/

.top-bar {
  position: relative;
  z-index: $navbar-z + 1;
  color: $color-topbar-text;
  background-color: $color-topbar-background;
  transition: background-color $transition-navbar 0.2s;

  @if ($color-topbar-background == $color-nav-background) {
    box-shadow: 0 -1px 0 0 rgba(contrast($color-nav-background, 100%), 0.1) inset;
  }

  @if ($font-secondary == "Oswald") {
    font-family: $font-secondary;
  }

  .icon {
    @extend %inline-middle;
    fill: currentColor;
  }

  .container {
    @include layout-group;
    position: relative;
    height: $topbar-height;
  }

  a {
    &:hover {
      color: $color-accent;
    }
  }

  .sticky & {
    @include media($min: $breakpoint-mobile-nav) {
      height: 0;
      overflow-y: hidden;
    }
  }

  .flyout-menu & {
    height: auto;
    overflow-y: auto;
    z-index: $alert-z + 1;
  }

  .site-branding-logo {
    max-height: rem-calc(80);
  }

  .search-wrap {
    position: relative;
  }
}

.top-bar-button {
  padding: 0;
  color: inherit;
  background: none;

  &:hover,
  &:focus {
    color: $color-accent;
    outline: none;
  }
}

.top-bar-left,
.top-bar-center,
.top-bar-right {
  @include layout-group-item;
  vertical-align: middle;
}

.top-bar-left,
.top-bar-right {
  .logo-align-center & {
    width: 35%;

    @include media($max: $breakpoint-mobile-nav) {
      display: none;
    }
  }

  .logo-align-center & {
    .flyout-menu & { display: none; }
  }
}


.top-bar-center {
  text-align: center;
  padding: rem-calc(10 0);

  .site-branding-logo { vertical-align: middle; }

  @include media($min: $breakpoint-mobile-nav) {
    width: 30%;
    padding: rem-calc(10) 0;
  }

  .flyout-menu & { width: 100%; }
}

.top-bar-right {
  text-align: right;

  .logo-align-right & {
    padding: 10px 0;

    @include media($max: $breakpoint-mobile-nav) { text-align: center; }
  }

  .logo-align-right.flyout-menu & {
    position: absolute;
    top: 50%;
    right: rem-calc(10);
    display: block;
    width: 100%;
    transform: translateY(-50%);

    @include media($max: $breakpoint-mobile-nav) { right: 0; }
  }

  .logo-align-left & {
    @include media($max: $breakpoint-mobile-nav) { display: none; }

    .flyout-menu & { display: none; }
  }
}

.top-bar-left {
  .site-branding { text-align: left; }

  .logo-align-left & {
    padding: 10px 0;

    @include media($max: $breakpoint-mobile-nav) { text-align: center; }
  }

  .logo-align-left.flyout-menu & {
    position: absolute;
    top: 50%;
    display: block;
    transform: translateY(-50%);
  }

  .logo-align-right & {
    @include media($max: $breakpoint-mobile-nav) { display: none; }

    .flyout-menu & { display: none; }
  }
}

.site-branding {
  margin: 0;
  text-align: center;
  font-size: rem-calc(24);
  font-family: $font-primary;
  letter-spacing: 0.05em;
  color: $color-site-branding;

  @if ($font-secondary == "Oswald") {
    font-family: $font-secondary;
  }

  .site-branding-link {
    display: inline-block;
    max-width: 80%;
    color: inherit;
    text-transform: uppercase;
    text-decoration: none;
  }
}

.customer-util {
  display: inline-block;
  vertical-align: middle;
}

.customer-links,
.currency-selector {
  display: inline-block;
}

.customer-links {
  position: relative;
  margin-right: rem-calc(25);
}

.dropdown-account .dropdown-panel {
  padding: 1rem;
  right: rem-calc(-21);
}

.currency-selector {
  @extend %placeholder-text-treatment;
  position: relative;
  top: rem-calc(2);
  margin-right: rem-calc(30);

  .currency-selector-active {
    margin-right: 5px;
    pointer-events: none;
  }

  .icon-arrow-down {
    display: inline-block;
    vertical-align: middle;
    margin: -0.3em 0 0 0.5em;
  }

  .currencies {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    color: $color-heading;
    opacity: 0;
    cursor: pointer;
  }
}

.button-wishlist {
  margin-right: rem-calc(25);
}

.customer-utils {
  text-align: right;
  @include media($max: $breakpoint-mobile-nav) { display: none; }

  .logo-align-right &,
  .logo-align-left & {
    display: inline-block;
    vertical-align: top;
    padding-top: 8px;
  }

  .icon-account {
    width: rem-calc(14);
    height: rem-calc(18);
  }

  .icon-wishlist {
    width: rem-calc(21);
    height: rem-calc(18);
  }

  .icon-cart {
    width: rem-calc(21);
    height: rem-calc(18);
  }
}

.button-cart-toggle {
  position: relative;
  transition: color 0.3s ease;

  @include media($max: $breakpoint-mobile-nav) {
    position: absolute;
    top: 50%;
    right: 10px;
    transform: translateY(-50%);
  }
}

.icon-cart-count {
  position: absolute;
  top: rem-calc(-15);
  left: rem-calc(12);
  display: block;
  width: 0;
  height: 0;
  font-size: rem-calc(9);
  color: $color-topbar-background;
  border: rem-calc(12) solid transparent;
  border-bottom-color: $color-accent;
  transform: scale(0);
  transition: transform 0.5s ease;

  &:after {
    position: absolute;
    top: rem-calc(12);
    left: rem-calc(-12);
    width: 0;
    height: 0;
    border: rem-calc(12) solid transparent;
    border-top-color: $color-accent;
    content: "";
  }

  &.show {
    transform: scale(1);
  }

  .number {
    position: absolute;
    z-index: 1;
    top: rem-calc(12);
    left: 50%;
    line-height: 0;
    transform: translateX(-50%);
  }
}

.mini-cart-subtotal {
  margin-right: rem-calc(6px);
  vertical-align: top;
  line-height: 1;
}


/*----------  Search Util  ----------*/

.search-wrap {
  .logo-align-right &,
  .logo-align-left &  {
    display: inline-block;
    margin-right: rem-calc(10);
    width: rem-calc(215);
  }

  &.search-compressed {
    .logo-align-right &,
    .logo-align-left &,
    .search-form {
      width: rem-calc(33);
    }

    .search-input {
      visibility: hidden;
    }
  }

  .search-form {
    margin: 0;
    width: 100%;
    transition: width 0.3s ease;

    .logo-align-right &,
    .logo-align-left & {
      .form-field > .form-input { padding-right: 45px; }
    }
  }

  .search-input {
    background-color: transparent;
    border: 0;
  }

  .form-field {
    margin: 0;
  }

  .button-search-toggle {
    vertical-align: middle;
  }

  .button-search-submit {
    color: $color-topbar-text;
  }

  .icon-search {
    width: rem-calc(18);
    height: rem-calc(18);
  }

  .form-inline-message {
    display: none;
  }
}

/*----------  Header Dropdowns  ----------*/

.dropdown-panel-tooltip {
  top: calc(100% + 20px);
  right: rem-calc(-16);
  left: auto;
  padding: 0;
  text-align: left;
  background-color: $color-background;
  border: 1px solid $color-border;
  transition: transform 0.3s ease;

  .logo-align-right & {
    right: rem-calc(-345);
  }

  &:before,
  &:after {
    position: absolute;
    right: rem-calc(345);
    bottom: 100%;
    width: 0;
    height: 0;
    pointer-events: none;
    border: solid transparent;
    content: "";

    .logo-align-left &,
    .logo-align-center & {
      right: rem-calc(16);
    }
  }

  &:before {
    border-bottom-color: $color-border;
    border-width: 11px;
  }

  &:after {
    border-bottom-color: $color-background;
    border-width: 10px;
    transform: translateX(-1px);
  }
}

/* scss-combine:layout/footer */
.site-footer {
  font-weight: 300;
  border-top: 1px solid $color-border;
  background-color: $color-footer-background;
  color: $color-footer-text;

  a {
    color: inherit;
    opacity: 0.65;

    &:hover {
      text-decoration: underline;
    }

    span:hover {
      color: $color-accent;
    }
  }

  .footer-title {
    margin-top: rem-calc(50);
    color: $color-footer-text;
  }

  .site-branding-link {
    opacity: 1;

    &:hover {
      color: $color-accent;
      text-decoration: none;
    }
  }

  .site-phone-number {
    cursor: default;

    &:hover {
      text-decoration: none;
    }
  }

  .container {
    @extend %clearfix;
  }

  .footer-col {
    &:last-child {
    }
  }

  .footer-nav {
    @include media($min: $breakpoint-s) {
      @include span(3);
    }

    @include media($max: $breakpoint-m, $min: $breakpoint-s) {
      @include last;
    }

    @include media($min: $breakpoint-m) {
      @include span(2);
    }

    li {
      line-height: 2.5;
    }
  }

  .navigation {
    ul ul li {
      line-height: 2;
    }
  }

  .dropdown-panel {
    position: static;
  }

  .footer-info {
    @include media($min: $breakpoint-s) {
      @include span(6);
    }

    @include media($min: $breakpoint-m) {
      @include span(4);
    }
  }

  .site-branding {
    text-align: left;
  }

  .footer-subscribe {
    @include media($min: $breakpoint-m) {
      @include span(4);
      @include last;
    }

    a {
      opacity: 0.5;
      transition: opacity 0.3s ease;

      &:hover {
        opacity: 1;
        text-decoration: none;
      }
    }
  }

  .footer-social,
  .footer-newsletter {
    @include media($max: $breakpoint-m, $min: $breakpoint-s) {
      @include gallery(6);
    }
  }

  .social-media-link {
    display: inline-block;
    margin-right: rem-calc(25);
  }

  .newsletter-signup {
    position: relative;

    .form-input {
      width: 100%;
      padding-right: rem-calc(50);

      @if (lightness($color-footer-background) < 75) {
        background-color: rgba(contrast($color-footer-background, 100%), 0.05);
        border-color: transparent;
      }
    }

    .submit {
      position: absolute;
      top: 0;
      right: 0;
      height: 100%;
      width: rem-calc(50);
      padding: 0;
      color: $color-border;
      line-height: 0;
      text-align: center;
      background-color: transparent;

      &:hover {
        .icon {
          transform: translateX(5px);
        }
      }
    }

    .icon {
      width: rem-calc(13);
      height: rem-calc(20);
      transition: transform 0.3s ease;
    }
  }

  .bottom-bar {
    margin-top: rem-calc(60);
    font-family: $font-secondary;
    font-size: rem-calc(12);
    text-transform: uppercase;
    letter-spacing: 0.167em;
    border-top: 1px solid rgba($color-border, 0.5);

    @include media($max: $breakpoint-s) {
      text-align: center;
    }

    @if ($font-secondary == "Playfair Display" or $font-secondary == "Lora") {
      font-family: $font-primary;
    }

    .credit-text,
    .sitemap-text {
      display: inline-block;
      padding: rem-calc(18) 0;
      margin: 0;
      font-size: rem-calc(12);
      letter-spacing: rem-calc(2);
      text-transform: uppercase;

      @include media($min: $breakpoint-s) {
        padding: rem-calc(46 0 36);
      }

      + .sitemap-text {
        display: block;

        @include media($min: $breakpoint-s) {
          display: inline-block;
        }

        &:before {
          @include media($min: $breakpoint-s) {
            padding: rem-calc(0 10);
            content: "- ";
          }
        }
      }

      &.align-center {
        display: block;
        padding-top: 0;
        text-align: center;
      }
    }
  }
}

.footer-payment-icons {
  text-align: center;
  padding: rem-calc(18 0 0);
  opacity: 0.5;

  @include media($min: $breakpoint-s) {
    padding: rem-calc(36) 0;
  }

  &.align-right {
    @include media($min: $breakpoint-s) {
      float: right;
      text-align: right;
    }
  }

  li {
    display: inline-block;
    width: 50px;
    margin: 0 5px;

    @include media($min: $breakpoint-s) {
      margin: 0 0 0 10px;
    }
  }

  svg {
    max-width: 80%;
  }
}


// Pages
/* scss-combine:pages/home */
/*----------  Carousel  ----------*/

.carousel {
  cursor: ew-resize;
  cursor: grab;

  .js & .carousel-item {
    opacity: 0;
    transition: opacity 0.6s ease;
  }

  .js &.slick-initialized .carousel-item {
    opacity: 1;
  }

  .container {
    position: relative;
  }

  &.single-slide { cursor: default; }
}

.carousel-item {
  width: 100%;
}

.carousel-item-image {
  @include maintain-ratio(2.4 1);
  background-size: cover;

  .ratio-natural & {
    height: auto;
    padding-bottom: 0;
  }

  .ratio-3-1 & { @include maintain-ratio(3 1); }

  .ratio-16-9 & { @include maintain-ratio(16 9); }
}

.home-carousel-image {
  @extend .sr-only;

  .ratio-natural & {
    position: static !important; //override !important in .sr-only
    display: block;
    width: 100%;
    height: auto;
  }
}

.carousel-item-info {
  padding: rem-calc(36) rem-calc(50) rem-calc(47);
  margin-top: rem-calc(-10);
  text-align: center;
  background-color: $color-background-secondary;
  opacity: 0;
  transition: all 0.3s ease-out;

  @include media($min: $breakpoint-s) {
    @include centerer;
    width: 100%;
    max-width: $carousel-caption-width;

    @if (lightness($color-background-secondary) < 75) {
      background-color: rgba($color-background-secondary, 0.75);
    } @else {
      background-color: rgba($color-background-secondary, 0.9);
    }
  }

  &.no-content {
    background-color: transparent;
    max-width: none;
  }

  &.show {
    margin-top: 0;
    opacity: 1;
  }

  .carousel-item-title {
    @extend %h1;
    position: relative;
    margin: 0;
    color: $color-carousel-title;

    &.show-slide-number {
      &:before {
        position: absolute;
        font-size: rem-calc(60);
        opacity: 0.15;
        content: attr(data-slide-number);
        transform: translate(-36%, -50%);

        @include media($min: $breakpoint-s) {
          font-size: rem-calc(64);
        }
      }
    }
  }

  .carousel-item-caption {
    margin-bottom: 1.5em;
    font-weight: 300;
    color: rgba($color-carousel-text, 0.75);
  }

  .button {
    @include media($max: $breakpoint-s) {
      background-color: transparent;
      box-shadow: 0 0 0 1px contrast($color-background-secondary, 100%) inset;

      &:hover {
        color: contrast($color-background-secondary, 100%);
      }
    }
  }
}

// Styles to prevent carousel info from bleeding over the edges of short carousel images
.carousel-short-image {
  max-height: 90%;

  .carousel-item-title { font-size: rem-calc(45); }

  .carousel-item-caption {
    margin-bottom: 1em;
  }
}

.carousel-navigation-item {
  position: absolute;
  top: rem-calc(150);
  z-index: 1;
  padding: rem-calc(19 24 20);
  margin-top: rem-calc(-30);
  color: contrast($color-background-secondary, 100%);
  line-height: 0;
  background-color: darken(saturate(adjust-hue($color-background-secondary, -1.1483), 0.1678), 3.3333);
  cursor: pointer;
  transition: background-color 0.3s ease-out;

  @include media($min: $breakpoint-s) {
    margin-top: 0;
    top: auto;
    bottom: 0;
  }

  &:hover {
    background-color: $color-accent;
  }

  &.previous { left: 0; }
  &.next { right: 0; }

  .icon {
    width: 12px;
    height: 21px;
  }

  .single-slide & { display: none; }
}

/*----------  Featured Products  ----------*/

.products-featured {
  .product-grid-item {
    margin-bottom: 2.5rem;

    @include media($min: $breakpoint-xs) {
      @include gallery(6 of 12);
      }

      @include media($min: $breakpoint-s) {
        @include gallery(3 of 12);
    }
  }

  .product-item-brand {
    display: none;
  }
}


/*----------  New Products  ----------*/

.products-new,
.products-best {
  background-color: $color-background-tertiary;

  .product-grid-item {
    @include media($min: $breakpoint-s) {
      @include gallery(4 of 12);
    }
  }

  .product-item-title {
    @extend %h2;
    margin: 0 0 rem-calc(4);
  }
}


/*----------  Blog Recent Posts  ----------*/

.blog-latest-posts {
  .post {
    @include media($min: $breakpoint-s) {
      @include gallery(6);
    }
  }

  .post-thumb {
    @include maintain-ratio($ratio: 584 440);
  }

  .post-meta { margin-bottom: rem-calc(5); }

  .post-title {
    margin-top: 0;
    @extend %h4;
  }
}

.post-summary-hide { display: none; }

.post-read-more {
  @extend %link-style;
  display: inline-block;
  margin-top: 20px;
  text-decoration: none;
}

/* scss-combine:pages/compare */
/*----------  Top-bar Tab  ----------*/

.compare-wrap {
  position: static;
  display: none;
  padding-right: $double-gutter;
  margin-right: $double-gutter;
  border-right: 1px solid $color-border;

  &.compare-active {
    display: inline-block;
  }

  .compare-tab {
    width: $mini-cart-width;
    top: rem-calc(79);
    right: 0;
    min-width: 0;

    &:before,
    &:after {
      right: 76%;
    }
  }
}

.button-compare-toggle {
  @extend %placeholder-text-treatment;
}

.compare-items-count {
  &:before {
    content: "(";
  }

  &:after {
    content: ")";
  }
}

.compare-tab-upper {
  padding: rem-calc(20 20 0);
}

.compare-items-list {
  margin-top: $gutter;
}

.compare-tab-item {
  @extend .mini-cart-item;
  display: none;

  &.animating,
  &.visible {
    display: block;
  }

  &.animating-in,
  &.animating-out {
    transform: translateY(-10%);
    opacity: 0;
    transition: opacity 0.2s ease, transform 0.3s ease;
  }

  &.visible {
    transform: translateY(0);
    opacity: 1;
  }
}

.compare-tab-item-wrap {
  @extend .mini-cart-item-wrap;
}

.compare-tab-item-thumb {
  @extend .mini-cart-item-thumb;
}

.compare-tab-item-description {
  @extend .mini-cart-item-description;
  padding-right: $gutter;
  text-align: left;
}

.compare-tab-item-title {
  @extend .mini-cart-item-title;
}

.compare-tab-item-remove {
  position: absolute;
  top: rem-calc(20);
  right: rem-calc(20);
  line-height: 0;
  cursor: pointer;

  .icon-close {
    width: rem-calc(7);
    height: rem-calc(7);
  }
}

.compare-tab-lower {
  padding: rem-calc(20);
  text-align: center;
}

.compare-submit {
  width: 100%;

  &:before {
    content: attr(data-compare-text);
  }

  &.is-disabled {
    pointer-events: none;

    &:before {
      content: attr(data-add-more-text);
    }
  }
}

/*----------  Compare Grid Item  ----------*/

.product-item-compare {
  .icon-check-mark {
    position: static;
    display: none;
    width: 12px;
    height: 8px;
    margin-right: 5px;
  }

  .input-checkbox:checked {
    & + span {
      .icon-check-mark {
        display: inline-block;
      }
    }
  }

  .form-label-text {
    margin: 0;
  }
}


/*----------  Compare Page  ----------*/

.compare-header {
  padding-bottom: 0;
}

.compare-content {
  margin-bottom: 200px;
  overflow: auto;

  .product-item-title {
    @extend %h4;
    margin: 0;
  }

  .product-item-price {
    margin-bottom: rem-calc(15);
    font-weight: normal;
  }

  .button {
    min-width: rem-calc(205);
    margin-top: rem-calc(15);
  }

  .add-to-cart {
    float: none;
    margin: 0.9375rem 0 0;
  }
}

.compare-table {
  border-color: transparent;

  .product-item-thumbnail {
    display: inline-block;
    width: 100%;
  }

  .product-item-thumbnail-image {
    @include maintain-ratio(1 1);
    background-repeat: no-repeat;
    background-position: 50% 50%;
    background-size: contain;

    .thumbnail-cover & {
      background-size: cover;
    }
  }
}

.compare-row {
  border-top-color: $color-border;

  &:first-child {
    border-color: transparent;
  }
}

.compare-main-row {
  text-align: center;
}

.compare-table-heading {
  @extend %h6;
  width: 20%;
  padding-left: 0;
  vertical-align: top;
  text-align: left;
  text-transform: none;

  @include media($max: $breakpoint-mobile-nav) {
    position: absolute;
  }
}

.compare-description-row,
.compare-rating-row {
  height: rem-calc(60);
}

.compare-table-cell {
  vertical-align: top;
}

.compare-product-main-cell {
  padding-bottom: rem-calc(60);
  vertical-align: bottom;
}

.compare-remove {
  display: block;
  margin-top: $gutter;
}

/* scss-combine:pages/cart */
.cart-title {
  &.cart-has-items {
    @extend %section-title-text-treatment;
    @extend %h3;
  }
}

.cart {
  .link {
    font-size: rem-calc(12px);
    cursor: pointer;
  }
}

.cart-table-header,
.cart-item {
  @include media($min: $breakpoint-s) {
    @include layout-group();
  }

  .cell {
    @include media($min: $breakpoint-s) {
      @include layout-group-item();
      padding-right: $gutter;
      vertical-align: middle;
    }

    &:last-child {
      padding-right: 0;

      @include media($min: $breakpoint-s) {
        text-align: right;
      }
    }
  }
}

.cart-empty {
  text-align: center;
  padding-bottom: 0;
}


/*----------  Cart Table Header  ----------*/

.cart-table-header {
  padding-bottom: 25px;
  border-bottom: 1px solid $color-border;

  @include media($max: $breakpoint-s) {
    display: none;
  }

  .cell {
    @extend %h5;
    color: $color-bold;
  }
}

.cart-item-options {
  @include media($min: $breakpoint-s) {
    width: 20%;
  }

  @include media($min: $breakpoint-m) {
    width: 30%;
  }
}

.cart-item-quantity,
.cart-item-price {
  @include media($min: $breakpoint-s) {
    width: 15%;
  }

  @include media($min: $breakpoint-m) {
    width: 10%;
  }
}

.cart-item-quantity {
  .quantity-control {
    .form-input {
      max-width: rem-calc(60);
    }
  }
}


/*----------  Cart Table Items  ----------*/

.cart-item {
  padding: rem-calc(25px) 0;
  border-bottom: 1px solid $color-border;

  @include media($max: $breakpoint-s) {
    position: relative;
  }

  .cell {
    @include media($max: $breakpoint-s) {
      margin-bottom: 1em;
    }
  }

  .product-option {
    margin: rem-calc(10) 0;
  }

  .option-label:not(:last-child) {
    &:after {
      content: ", ";
    }
  }
}

.cart-item-info {
  margin-bottom: $gutter;

  @include media($min: $breakpoint-xs) {
    margin-bottom: 24px;
  }

  @include media($min: $breakpoint-s) {
    width: 50%;
    margin-bottom: 0;
  }

  .product-brand {
    @extend %h6;
  }

  .product-name {
    @extend %h4;
    margin-top: 0.25em;
    margin-bottom: 0.35em;
    display: block;
    color: $color-bold;
    text-transform: uppercase;
  }
}

.cart-item-info-wrap {
  @include layout-group;
}

.cart-item-image,
.cart-item-text {
  @include layout-group-item;
  vertical-align: middle;
}

.cart-item-image {
  width: rem-calc(138);

  img {
    max-width: 100%;
  }
}

.cart-item-text {
  padding-left: 20px;

  @include media($min: $breakpoint-xs) {
    padding-left: $double-gutter;
  }
}

.cart-item-price,
.cart-item-quantity {
  @include media($max: $breakpoint-s) {
    margin-left: rem-calc(168);
    color: $color-bold;
  }
}

.cart-item-actions .link { margin-right: rem-calc(8px); }

.cart-label {
  margin-right: rem-calc(5px);
  color: $color-bold;

  .cart-item-quantity &,
  .cart-item-price & {
    @include media($min: $breakpoint-s) {
      display: none;
    }
  }

}

.cart-item-price {
  @include media($min: $breakpoint-s) {
    position: relative;
  }

  .price-discounted {
    text-decoration: line-through;
  }
}

.cart-item-discount {
  @include media($min: $breakpoint-s) {
    display: block;
  }
}

.cart-item-update {
  display: block;
  visibility: hidden;
  opacity: 0;
  transition: opacity 0.3s ease;

  .needs-updating & {
    visibility: visible;
    opacity: 1;
  }

  @include media($min: $breakpoint-s) {
    position: absolute;
    right: 0;
  }
}

.cart-item-remove {
  position: absolute;
  top: rem-calc(25px);
  right: 0;
  color: inherit;

  @include media($min: $breakpoint-s) {
    top: 0;
  }

  @include media($min: $breakpoint-l) {
    top: 50%;
    right: rem-calc(-53px);
    transform: translateY(-50%);
  }

  .icon {
    width: 12px;
    height: 12px;
  }
}

/*----------  Gift Wrapping  ----------*/

.giftwrapping-detail {
  color: $color-meta;
  font-size: rem-calc(13);
}

.giftwrap-modal {
  max-width: $container-width-narrow;

  .form-field-title {
    margin-left: 0;
    display: block;
  }

  .form-radio + .form-label-text {
    font-weight: 300;
  }

  .form-input:not(.form-radio) {
    width: 100%;
  }
}

.giftwrap-subtitle,
.giftwrap-title {
  text-align: center;
}

.giftwrap-title {
  margin-top: 0;
}

.giftwrap-subtitle {
  @extend %h6;
  margin-bottom: 1.5em;
  color: $color-meta;
}

/*----------  Cart Totals  ----------*/

.cart-totals {
  @extend %clearfix;
}

.cart-totals-column {
  @include media($min: $breakpoint-s) {
    @include span(45%);
  }

  @include media($min: $breakpoint-m) {
    @include span(37.5%);
  }

  &:first-child {
    .cart-totals-item {
      border-bottom: 0;

      @include media($min: $breakpoint-s) {
        border: 0;
      }
    }

    .gift-certificates {
      padding-top: 0;
    }
  }

  &:last-child {
    @include media($min: $breakpoint-s) {
      @include last;
    }
  }
}

.cart-totals-item {
  @extend %clearfix;
  padding: rem-calc(38) 0;
  border-bottom: 1px solid $color-border;

  .link {
    position: relative;
    top: rem-calc(9);
    float: right;
    clear: right;
  }

  .cart-total-item {
    & + .link {
      span { display: none; }

      &:before {
        content: attr(data-change-text);
      }
    }
  }
}

.cart-total-label {
  float: left;
  font-weight: normal;
}

.cart-total-item {
  float: right;

  &.grand-total {
    font-weight: bold;
    color: $color-bold;
  }
}

.list-bulleted .link {
  display: block;
}

.cart-actions {
  @include media($max: $breakpoint-xs) {
    .button {
      display: block;
      margin: 2rem 0;
    }
  }

  @include media($min: $breakpoint-xs) {
    @include layout-group($gutter: 2rem);
    margin: 2rem 0 0 -2rem;

    .button {
      @include layout-group-item;
      width: 50%;
      padding-right: 0;
      padding-left: 0;
    }
  }
}

// Coupons

.form-coupon {
  position: relative;
}

.coupons-active {
  margin-bottom: 0;
}

// Shipping

.shipping-calculator {
  @extend %clearfix;
  margin-top: 1.75rem;

  &.visible {
    display: block;
  }

  .shipping-zip {
    float: left;
    padding-left: rem-calc($account-gutter-inner * 2);
  }

  .get-shipping-quotes {
    float: right;
  }
}

.shipping-state-wrapper {
  margin-left: 1rem;

  .form-input:not(.form-select) {
    width: 100%;
  }
}

.shipping-quotes {
  clear: both;
}

.shipping-quote-list {
  margin-bottom: 3em;
}

.shipping-quote-item {
  cursor: pointer;
  margin-left: 2px;

  &:hover {
    color: $color-accent;
  }

  .form-label-text {
    @extend %body-text-treatment;
    margin-bottom: 0;
    margin-left: rem-calc(20);
  }

  .cost {
    float: right;
    color: $color-bold;
  }
}

.shipping-logo {
  height: 100%;
  vertical-align: middle;
  margin-right: 3px;
}

// Gift Certificate

.form-gift-certificate {
  margin-top: 1.75rem;
}

.additional-checkout-buttons {
  @extend %clearfix;
  text-align: center;

  @include media($min: $breakpoint-xs) {
    float: right;
    width: calc(50% - 1rem);
  }

  // scss-lint:disable ImportantRule, QualifyingElement
  p {
    float: none !important;
  }

  form { clear: both;}
}

.modal-edit-options {
  .title,
  .alert {
    margin-bottom: 2rem;
  }
}

/* scss-combine:pages/account */
.account-wrapper {
