/*----------  Forgot Password  ----------*/

.auth-forgot-password-message { text-align: center; }

.auth-forgot-password {
  .form { @include layout-group; }

  .form-field,
  .form-actions {
    @include layout-group-item;
    vertical-align: bottom;
  }

  .form-actions {
    width: 33%;
    text-align: right;
  }
}

.account-page-forgot {
  padding-right: $double-gutter;
  padding-left: $double-gutter;
}

.account-forgot-form .form-field .form-inline-message {
  position: absolute;
}


/*----------  Products  ----------*/

.account-product-item {
  @extend .product-grid-item;
  margin-bottom: 3rem;

  @include media($min: $breakpoint-xs) {
    @include gallery(6 of 12);
  }

  @include media($min: $breakpoint-m) {
    @include gallery(4 of 12);
  }

  .price {
    [data-price] {
      display: block;
    }
  }
}

.account-product-item-title {
  @extend .product-item-title;
}

.account-product-item-brand {
  @extend .product-item-brand;
}

/* scss-combine:pages/blog */
/*----------  Blog Index  ----------*/

.blog .page-header {
  padding-bottom: 0;
}

.blog-post-index {
  padding-bottom: rem-calc(120);
}

.blog-items-list {
  @extend %clearfix;

  .post {
    margin-bottom: rem-calc(90);

    @include media($min: $breakpoint-s) {
      @include gallery(6);
    }

    @include media($min: $breakpoint-m) {
      @include gallery(4);
    }
  }

  .post-thumb {
    @include maintain-ratio($ratio: 378 285);
    margin-bottom: 1rem;
  }

  .post-title {
    @extend %h4;
    margin-top: 0;
    margin-bottom: 1rem;
  }
}

.post-thumb {
  @extend %background-position-img;
  display: block;

  img { @extend .show-for-sr; }
}

.post-meta {
  @extend %h6;
  margin-bottom: rem-calc(5);
}

/*----------  Blog Single  ----------*/

.blog-post-single {
  .post-title {
    margin-bottom: 0;
  }

  .post-meta {
    margin-bottom: rem-calc(45);
  }

  .post-main {
    margin: 0 auto;
  }

  .post-footer {
    margin: rem-calc(90 0 120);
  }

  .share-buttons-nav {
    display: inline-block;
    vertical-align: middle;
  }

  .featured-image {
    display: block;
    margin: 0 auto rem-calc(40);
    width: 100%;
  }
}

.post-footer-label {
  @extend %h6;
  margin-right: rem-calc(10)
}

.post-tags {
  margin-bottom: $double-gutter;
}

.post-tag {
  @extend %link-style;
  margin-right: rem-calc(6);
}

/*----------  Share Buttons  ----------*/

.share-buttons { line-height: 0; }

.social-link {
  display: inline-block;
  margin-left: rem-calc(10);
  vertical-align: middle;

  &.mail {
    .icon {
      height: rem-calc(16);
    }
  }

  a {
    color: $color-meta;

    &:hover {
      color: $color-accent;
    }
  }

  .icon {
    width: rem-calc(30);
    height: rem-calc(16);
  }
}

.fb-like iframe { max-width: none; }

/* scss-combine:pages/category */
.category-nav {
  margin-bottom: 4em;
}

.category-nav-list {
  .category-nav-list {
    padding-left: $gutter;
    margin-top: 1rem;
  }
}

.category-nav-list-item {
  @extend .facet-item;
}

.category-banner-image {
  @include maintain-ratio(2.25 1);
  position: relative;
  background: {
    size: cover;
    position: 50%;
  }

  .category-banner-caption {
    @extend .carousel-item-info;
    width: auto;
    padding: 2.25rem 3.125rem;
    margin-top: 0;
    opacity: 1;

    .show-title-x & {
      padding-top: 4.5rem;
      background-color: $color-background;
    }
  }

  .section-title {
    width: auto;
    padding: 0;
    margin: 0;
    border: 0;

    body:not(.show-title-x) & {
      color: $color-carousel-title;
    }
  }
}

/* scss-combine:pages/brands */
.brand-listing {
  padding-top: 0;
}

.brand-items-list {
  font-size: 0;
}

.brand-item {
  display: inline-block;
  width: 50%;
  padding: $gutter;
  font-size: $font-size-body;
  text-align: center;
  vertical-align: bottom;

  @include media($min: $breakpoint-s) {
    width: span(25%);
  }

  a {
    position: relative;
    display: block;
    padding: 50% 0;
    color: inherit;
    border: 1px solid $color-border;
    transition: all 0.6s ease-out;

    &:hover {
      background-color: $color-bold;
      color: contrast($color-bold, 100%);
      transition: all 0.3s ease-out
    }

    .brand-name {
      position: absolute;
      max-width: 75%;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
    }
  }
}

.brands {
  .pagination {
    margin: rem-calc(50px) 0 rem-calc(80px);
  }
}

/* scss-combine:pages/search */
.search-query-prefix {
  color: $color-body;
}

.search-header {
  padding-bottom: rem-calc(65);

  .container {
    position: relative;
  }

  .search-form {
    @include media($min: $breakpoint-s) {
      position: absolute;
      bottom: 0;
      width: span(3);

      .form-field {
        margin: 0;
      }
    }

    .form-field {
      width: 100%;
    }
  }
}

.search-tab-title {
  display: inline-block;

  &.active,
  &:hover {
    color: $color-accent;
    text-decoration: underline;
  }
}

.results-count {
  font-size: rem-calc(12);
}

.search-results {
  min-height: rem-calc(100);

  .post {
    position: relative;
    padding-top: rem-calc(45);
    margin-bottom: rem-calc(45);

    @include media($min: $breakpoint-s) {
      @include gallery(6);

      &:before {
        position: absolute;
        top: 0;
        left: 0;
        display: block;
        border-top: 1px solid $color-border;
      }

      &:nth-child(2n+1):not(:first-child) {
        &:before {
          right: calc(-100% - #{gutter() * 2}); // top border on second+ rows that spans content width
          content: "";
        }
      }
    }

    @include media($min: $breakpoint-m) {
      @include gallery(4);

      &:nth-child(2n+1) {
        &:before {
          display: none;
        }
      }

      &:nth-child(4n+4) {
        &:before {
          right: calc(-200% - #{gutter() * 6});
          content: "";
        }
      }
    }
  }

  .post-type {
    @extend %h6;
    margin-bottom: rem-calc(5);
  }

  .post-title {
    @extend %h4;
    margin-top: 0;
  }
}

.content-results {
  @extend %clearfix;

  .pagination {
    margin: 3.125rem 0 5rem;
    clear: both;
  }
}

/* scss-combine:pages/contact-us */
.form-contact-us {
  @extend %form-base-style;

  .form-input { width: 100%; }

  .form-field-common:not(.form-field-multiline) {
    @include media($min: $breakpoint-s) {
      width: 48%;
      float: left;

      &:nth-child(odd) {
        margin-left: 4%;
      }
    }
  }

  .form-field-captcha,
  .form-field-multiline {
    clear: left;
  }
}

/* scss-combine:pages/gift-certificates */
.giftcard-sidebar {
  @include media($min: $breakpoint-s) {
    @include span(3);
    padding-right: $double-gutter;
  }
}

.giftcard-content {
  padding-bottom: $section-padding;

  @include media($min: $breakpoint-s) {
    @include span(9);
    @include last;
  }

  .giftcard-subheading {
    margin: 1.5em 0;
  }
}

.giftcard-header {
  @extend h2;
  padding-bottom: rem-calc(10);
  border-bottom: 1px solid $color-border;
}

.giftcard-nav {
  @extend .breadcrumbs;
  margin: 0;
  padding: 0;
}

.giftcard-nav-item {
  @extend .breadcrumb;
  margin: 0;
  color: inherit;

  &:hover,
  &.is-active {
    color: $color-accent;
  }
}

.giftcard-heading-container {
  margin-bottom: rem-calc(50);
}

.giftcard-heading {
  @extend %h3;
}

.giftcard-subheading {
  @extend %h6;
}

.giftcard {
  padding-top: 0;

  .alert {
    margin-bottom: 2em;
  }
}

.giftcard-body {
  .form-input:not(.form-radio) {
    width: 100%;
  }
}

.giftcard-form-preview {
  iframe {
    width: 100%;
  }
}

.form-giftcard-balance {
  @include layout-group;

  .form-field,
  .form-actions {
    @include layout-group-item;
    vertical-align: bottom;
  }

  .form-field {
    width: 90%;
    padding-right: rem-calc(30);
  }

  .form-actions {
    width: auto;
  }
}

.giftcard-form-theme {
  .form-field {
    @extend %clearfix;
  }

  .form-label {
    @include gallery(4);
  }
}

.giftcard-form-actions {
  text-align: center;
}


#giftcard-amount {
  @include media($min: $breakpoint-s) {
    width: calc(50% - 8px);
  }
}

.giftcard-form-help-message {
  @include media($min: $breakpoint-s) {
    position: relative;
    top: -40px;
    margin-left: calc(50% + 15px);
  }
}

.giftcard-redeem-steps {
  margin-left: 1em;
}


/* scss-combine:pages/sitemap */
.sitemap-group {
  .page-content & {
    ul,
    li {
      margin-bottom: 0;
    }
  }
}

.sitemap-show-all {
  margin-top: rem-calc(12);
  list-style: none;
}
