components/header/currency-selector:
{{#if currency_selector.currencies.length ">" 1}}
  <div class="currency-selector customer-util" data-currency-selector>
    <span class="form-selected-text" data-currency-selector-toggle>
      {{currency_selector.active_currency_code}}
    </span>
    <i class="icon icon-arrow-down"></i>
    <select class="currencies">
      {{#each currency_selector.currencies}}
        <option value="{{{switch_url}}}" {{#if is_active}}selected{{/if}} data-value="{{currency-code}}">
          {{name}}
        </option>
      {{/each}}
    </select>
  </div>
{{/if}}

components/common/main-navigation-catego:
{{#each categories}}
  <li class="main-nav-item nav-page {{#if children}}dropdown{{/if}}">
    <a class="{{#if children}}dropdown-toggle{{/if}} top-level-nav-link items" href="{{url}}">
      <span>{{name}}</span>
      {{#if children}}
        <svg class="icon icon-arrow-down"><use xlink:href="#icon-arrow-down" /></svg>
      {{/if}}
    </a>
    {{#if children}}
      <ul class="dropdown-panel">
        <li class="parent-all"><a href="{{url}}">{{lang 'common.navigation_all' name=name}}</a></li>
        {{#each children}}
          <li>
            <a href="{{url}}">{{name}}</a>
            {{#if children}}
              <ul class="submenu-dropdown-panel">
                {{#each children}}
                  <li><a href="{{url}}">{{name}}</a></li>
                {{/each}}
              </ul>
            {{/if}}
          </li>
        {{/each}}
      </ul>
    {{/if}}
  </li>
{{/each}}

components/common/navigation-mega:
{{!
  @param nav_items
         > either "pages" or "categories", passed from parent template
}}

{{#each nav_items}}
  <li class="parent {{#if children}}has-children{{/if}}">
    <a class="{{#if image}}has-image{{/if}}" href="{{url}}">{{name}}</a>
    {{#unless ../theme_settings.meganav-show-top-level-only}}
      {{#if children}}
        <ul class="children">
          {{#each children}}
            <li>
              <a class="{{#if image}}has-image{{/if}}" href="{{url}}">{{name}}</a>
              {{#if children}}
                <ul>
                  {{#each children}}
                    <li><a href="{{url}}">{{name}}</a></li>
                  {{/each}}
                </ul>
              {{/if}}
            </li>
          {{/each}}
        </ul>
      {{/if}}
    {{/unless}}
  </li>
{{/each}}

components/common/main-navigation-pages:
{{#each pages}}
  {{#if children}}
    <li class="main-nav-item nav-page dropdown">
      <a class="{{#if children}}dropdown-toggle{{/if}} top-level-nav-link items" href="#">
        <span>{{name}}</span>
        <svg class="icon icon-arrow-down"><use xlink:href="#icon-arrow-down" /></svg>
      </a>
      <ul class="dropdown-panel">
        <li class="parent-all"><a href="{{url}}">{{name}}</a></li>
        {{#each children}}
          <li>
            <a href="{{url}}">{{name}}</a>
            {{#if children}}
              <ul class="submenu-dropdown-panel">
                {{#each children}}
                  <li><a href="{{url}}">{{name}}</a></li>
                {{/each}}
              </ul>
            {{/if}}
          </li>
        {{/each}}
      </ul>
    </li>
  {{else}}
    <li class="main-nav-item nav-page">
      <a class="top-level-nav-link items" href="{{url}}">
        <span>{{name}}</span>
      </a>
    </li>
  {{/if}}
{{/each}}
